INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(1, '10.1093/aje/kwu275', 25395026, 'Turner MC, Cohen A, Jerrett M, Gapstur SM, Diver WR, Pope CA 3rd, Krewski D, Beckerman BS, Samet JM.', 'Turner', FALSE, 'Interactions Between Cigarette Smoking and Fine Particulate Matter in the Risk of Lung Cancer Mortality in Cancer Prevention Study II.', 'Am J Epidemiol. 2014; 180 (12): 1145-1149.', 2014, STRINGDECODE('Neue Analyse der Daten der amerikanischen Krebspr\u00e4ventions-Kohertenstudie zur Untersuchung, wie gross das kombinierte Krebsrisiko durch Feinstaub ist.'), STRINGDECODE('429''406 Teilnehmer, Frauen und M\u00e4nner aus 50 Staaten der USA, welche in den Jahren 1982/1983 im Alter von mindestens 30 Jahren f\u00fcr die Krebsvorsorgestudie der amerikanischen Kriegsgesellschaft (ACS) rekrutiert worden waren, in den Jahren 1984, 1986 und 1988 wieder kontaktiert worden waren, und seit 1989 mit dem nationalen Sterberegister auf ihr \u00dcberleben kontaktiert wurden. Nicht in diese Analyse einbezogen wurden Exrauchende und Pfeifen- oder Zigarrenraucher. USA.'), '', '', '', '', '', STRINGDECODE('Da nur bis 1998 individuelle Informationen \u00fcber das Rauchverhalten vorlagen, wurden nur die ersten 6 Studienjahre in diese Analyse einbzeogen. Die Absch\u00e4tzung der Belastung mit Feinstaub wurde mit Landnutzungsmodellen f\u00fcr die geocodierten Adressen bei Studieneintritt vorgenommen, welche sich auf Monatsmittelwerte von PM2.5 der Jahre 1999-2004 von 1464 Messstationen abst\u00fctzten, unter der Annahme, dass die Feinstaubbelastungen \u00fcber die Jahre eng korreliert seien. Mit proportionalen Hazard-Modellen nach Cox, stratifiziert f\u00fcr Alter, Geschlecht und Rasse wurde das \u00dcberleben bzw. die Sterblichkeit an Lungenkrebs in den ersten 6 Jahren in Abh\u00e4ngigkeit der PM2.5-Belastung in verschiedenen Kategorien (\u00fcber/unter der 50 Perzentile, \u00fcber der 66. vs. unter der 33. Perzentile, sowie \u00fcber der 75. vs. unter der 25. Perzentile) und in Abh\u00e4ngigkeit von Rauchen/nicht Rauchen modelliert. Einbezogen wurden folgende invidivuellen Faktoren: Schulbildung, Zivilstand, BMI, Passivrauchen, Ern\u00e4hrung, Alkoholkonsum und berufliche Belastung. Die Effektmodifikation bez\u00fcglich Lungenkrebsterblichkeit wurde mit drei Gr\u00f6ssen untersucht: das relative zus\u00e4tzliche Risiko durch die Interatkion (RERI), der der Interaktion anrechenbare Teil des Risikos (AP) und der Synergie-Index (SI). Lungenkrebs, Kohortenstudie, Statistik, epidemiologische Methoden. ACS-Studie. USA.'), '', '', '', '', STRINGDECODE('In 2''509''717 Personen-Jahren der Nachkontrolle ereigneten sich 1921 Todesf\u00e4lle an Lungenkrebs. Die gesch\u00e4tzte Feinstaubbelastung lag im Durchschnitt bei 12.6 SD 2.85 \u00b5g PM2.5/m3, mit der 25. und 75. Perzentile bei 10.59 und 14.44 \u00b5g PM2.5/m3. Raucher hatten im Vergleich zu Nichtrauchern ein 13.5 fach erh\u00f6htes Risiko (95%CI 10.2-17.9), an Lungenkrebs zu sterben, wenn ihre PM2.5-Belastung gering war, d.h. unter der 25. Perzentile lag. Nichtraucher hatten ein 1.28 faches Risiko (0.92-1.78), wenn ihre Belastung \u00fcber der 75. Perzentile der PM2.5-Belastung lag, im Vergleich zu Nichtrauchern mit geringer Belastung. Raucher hatten ein 16 faches Risiko (12.1-21.1), an Lungenkrebs zu sterben, wenn ihre Feinstaubbelastung \u00fcber der 75. Perzentile lag. Das zus\u00e4tzliche relative Risiko durch die Interaktion (RERI) f\u00fcr die Kombination von Rauchen und schlechter Luft betrug 2.19 (-0.10;+4.83). Der Risikoanteil, der dem Kombinationseffekt angerechnet werden kann, betrug 14%, der Synergie-Index 1.17. Die Autoren schliessen daraus, dass die Folgen von Rauchen und Luftverschmutzung st\u00e4rker zusammenwiren als nur additiv. Auch wenn die Lungenkrebf\u00e4lle am st\u00e4rksten durch einen R\u00fcckgang des Rauchens abnehmen, kann ein solcher R\u00fcckgang mit einer Verbesserung der Luftqualit\u00e4t st\u00e4rker ausfallen als mit einer der beiden Massnahmen allein.'), '', '', 'Kommentar von Panagiotou AO, Wacholder S: How Big Is That Interaction (in My Community)-and I. Which Direction? Am. J. Epidemiol. 2014 180: 1150-1158.', '', '1F', 1, TIMESTAMP '2016-12-14 14:47:29.431', 1, TIMESTAMP '2016-12-14 14:47:29.431', 1, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(2, '10.1161/ATVBAHA.116.307141', 27312220, 'Dorans KS, Wilker EH, Li W, Rice MB, Ljungman PL, Schwartz J, Coull BA, Kloog I, Koutrakis P, D''Agostino RB Sr, Massaro JM, Hoffmann U, O''Donnell CJ, Mittleman MA.', 'Dorans', FALSE, 'Residential Proximity to Major Roads, Exposure to Fine Particulate Matter, and Coronary Artery Calcium: The Framingham Heart Study.', 'Arterioscler Thromb Vasc Biol. 2016 Aug;36(8):1679-85', 2016, STRINGDECODE('Kohortenstudie in den USA zur Untersuchung, ob die Verkalkung der Herzkranzgef\u00e4sse mit der Belastung mit PM2.5 oder der Distanz zu einer Autobahn zusammenh\u00e4ngt.'), STRINGDECODE('3399 Nachkommen (Kinder und Enkel, 51% Frauen) der Teilnehmer der Framingham-Kohorte, die bei der ersten von ein (2002-2005) bis zwei (2008-2011) Untersuchungen der Verkalkung der Herzkranzgef\u00e4sse CAC durchschnittlich 52.2 Jahre alt waren und im Nordosten der USA wohnten.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Die Verkalkung der Herzkranzgef\u00e4sse CAC wurde mit Elektronenstrahl-Computertomographie untersucht und die Agatstonpunktzahl als Zielgr\u00f6sse definiert. Ein Wert von mehr als Null wurde als Verkalkung interpretiert. Informationen zu weiteren Gesundheitsgr\u00f6ssen der Patienten stammten aus den aktuellsten Ergebnissen der 4 j\u00e4hrlichen Gesundheitsuntersuchungen.\r\nAls Belastung mit Verkehr wurde die Distanz der Adresse zum Zeitpunkt der ersten Untersuchung zur n\u00e4chsten Autobahn oder einer anderen gr\u00f6sseren Verkehrsachse (Klassen A1-A3, Entfernung von max. 50, 50-200, 200-400 und 400-1000m) definiert. Ausserdem wurde die Feinstaubbelastung mit einem Landnutzungsmodell f\u00fcr PM2.5 f\u00fcr das Jahr 2003 und 2003-2009 bestimmt, das auf Satellitendaten mit einer Aufl\u00f6sung von 1 x 1 km basierte.\r\nDer Zusammenhang der Koronarverkalkung (bin\u00e4rer CAC-Score ja/nein, kontinuierlich) mit der Strassenn\u00e4he oder der Feinstaubbelastung wurde einerseits mit verallgemeinerten Sch\u00e4tzgleichungen mit logit Link andererseits mit gemischten linearen Modellen untersucht. Der Zusammenhang der fortschreitenden Verkalkung (1. zur 2. Untersuchung) wurde mit logistischer Regression und mit gemischten linearen Modellen untersucht. In alle Modelle wurden Alter, Geschlecht, BMI, Rauchen, Bildung, %Hausbesitzer auf Quartierebenen als Variable f\u00fcr den sozio\u00f6konomischen Status, Kohorte (Enkeln oder Kinder der Ursprungskohorte), Datum der CAC Untersuchung und Abstand zur Gesundheitsuntersuchung (Tage) einbezogen. Sensitivit\u00e4tsanalysen mit weiteren St\u00f6rgr\u00f6ssen (Diabetes, Bewegungsverhalten, hoher Blutdruck) und Interaktionen wurden durchgef\u00fchrt und es wurde versucht, eine nicht-lineare Dosis-Wirkungsbeziehung zu beschreiben.\r\nHerz-/Kreislaufkrankheiten, Arteriosklerose, Framingham. Kohortenstudie. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die Teilnehmer wohnten median 201m (Interquartilabstand IQR 359m) von einer grossen Strasse (USA: Kategorie A1-A3) entfernt und waren im Jahr 2003 mit median 10.7 (IQR 1.4) \u00b5g/m3 PM2.5 belastet. Bei 45% der Teilnehmer wurde Verkalkungen der Herzkranzgef\u00e4sse festgestellt, bei der zweiten Untersuchung waren es 50%. Der mediane Agatston Score betrug bei Erstuntersuchung 70.5 IQR 278.5, bei der zweiten Untersuchung nach durchschnittlich 6.1 Jahren 137.1 (419.4).\r\nPersonen in der N\u00e4he von Autobahnen oder mit h\u00f6heren Feinstaubbelastung hatten kein erh\u00f6htes Risiko f\u00fcr einen messbaren CAC-Score, also einer Verkalkung der Herzkranzgef\u00e4sse, das Risiko war tendenziell eher (paradox) reduziert, aber mit weiten Konfidenzintervallen: z.B. OR 0.95 (95%-CI: 0.74-1.22) f\u00fcr Personen die weniger als 50m von einer Autobahnentfernt wohnten gegen\u00fcber denen, die \u00fcber 400m entfernt wohnten. Es konnte auch kein Zusammenhang mit der H\u00f6he des Scores und der Belastung gefunden werden. Das Risiko f\u00fcr fortschreitende Koronararterienverkalkung hing ebenfalls nicht mit der Verkehrsn\u00e4he oder der Feinstaubbelastung zusammen. Sie hing paradox mit der logarithmierten Entfernung zur Strasse zusammen; steigender CAC-Agatstonscore von 2.2 (0.1-4.3) pro Jahr pro logarithmierter IQR-Entfernung zur Strasse. Die Ergebnisse blieben auch in den Sensitivit\u00e4tsanalysen robust.\r\nDie Autoren folgern, dass ihre Ergebnisse nicht f\u00fcr einen engen Zusammenhang der Koronarverkalkung mit der Verkehrs- oder Feinstaubbelastung sprechen, betonen aber, dass die Belastung generell tief war und wenig variierte.'), NULL, NULL, NULL, NULL, NULL, 6, TIMESTAMP '2016-12-14 14:47:29.434', 1, TIMESTAMP '2017-01-10 06:22:54.249', 2, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(3, '10.1161/JAHA.115.002301', 26607712, 'Hart JE, Puett RC, Rexrode KM, Albert CM, Laden F.', 'Hart', FALSE, 'Effect Modification of Long-Term Air Pollution Exposures and the Risk of Incident Cardiovascular Disease in US Women.', 'J Am Heart Assoc. 2015; 4 (12). pii: e002301.', 2015, STRINGDECODE('Kohortenstudie an Krankenschwestern zur Untersuchung von Herz-/Kreislauferkrankungen in Abh\u00e4ngigkeit von der langj\u00e4hrigen Belastung mit Feinstaub.'), STRINGDECODE('114''537 Krankenschwestern in den USA, welche erstmals 1976 im Alter von 30-55 Jahren und seither alle 2 Jahre nachbefragt wurden, von denen mindestens eine Adresse bekannt war, f\u00fcr welche Schadstoffwerte bekannt waren. Nurses Health Studie.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('In der Nachfolgezeit von 1988-2006 wurde die Inzidenz von erstmaligen Herz-/Kreislaufkrankheiten, nicht t\u00f6dlicher oder t\u00f6dlicher koronarer Herzerkrankung und Hirnschlag untersucht. Die Informationen stammten aus den Fragebogen und wurde mit Krankengeschichten, Autopsie und Todesurkunde \u00fcberpr\u00fcft. Das Luftqualit\u00e4tsmessnetz der US-EPA, das Projekt IMPROVE und verschiedene weitere Studien lieferten monatliche Messdaten f\u00fcr PM10, ab 1999 auch PM2.5. F\u00fcr die Jahre vor 1999 wurde PM2.5 aus PM10 abgeleitet unter Verwendung des PM10/PM2.5 Verh\u00e4ltnisses aus einem Modell, das auf Daten der Zeit nach 1999 basierte. Die Belastung mit der gr\u00f6beren Fraktion PM10-2.5 wurde aus der Differenz von PM10 und PM2.5 berechnet. Mit einem Landnutzungsmodell wurde den geocodierten Adressen (basierend auf der Postleitzahl) die Belastung kumuliert \u00fcber 18 Jahre (1988-2006) zugeordnet.\\\\nMit proportionalen Hazard-Modellen nach Cox wurde die Inzidenz an Herz-/Kreislaufkrankheiten, koronaren Herzerkrankungen und Hirnschlag in Abh\u00e4ngigkeit der Feinstaubbelastung untersucht. In einem Grundmodell wurden Alter, Jahr, Jahreszeit und geografische Region und im erweiterten Modell zus\u00e4tzlich Rasse, BMI, Wechseljahrstatus, Hormoneinnahme, Bluthochdruck, Hypercholesterin\u00e4mie, Diabetes, famili\u00e4re Anamnese von Herzinfarkt, Rauchen, als Indikator f\u00fcr den Sozialstatus die Schulbildung, Zivilstand und Beruf des Partners und der Sozialstatus im Quartier einbezogen. Ausserdem wurde die Interaktion mit dem Alter, Diabetes, Familienanamnese von Herzinfarkt und Rauchen gepr\u00fcft. Sensitivit\u00e4tsanalyen zur \u00dcberpr\u00fcfung der Robusheit wurden durchgef\u00fchrt. Kohortenstudie. Nurses Health. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('6''767 Frauen erkrankten an Herz-Kreislauferkrankungen, 3''878 an  koronaren  Herzkrankheiten und 3''295 erlitten einen Hirnschlag. Die Frauen waren durchschnittlich 68.6 (7.3) Jahre alt, \u00fcberwiegend weiss und verheiratet. Die durchschnittliche j\u00e4hrliche Belastung und die Standardabweichung (SD) betrug 22.2 (6.5) \u00b5g PM10/m3, 8.7 (4.5) \u00b5g PM2.5-10/m3 und 13.4 (3.3) \u00b5g PM2.5/m3.\r\nIm Grundmodell nahm das Risiko bei einem Anstieg von 10 \u00b5g/m3 aller Feinstaubfraktionen gering, aber statistisch nicht signifikant zu. Lediglich f\u00fcr einen Anstieg von PM10 und Herz-/Kreislaufkrankheiten und f\u00fcr PM2.5-10 mit allen drei Zielgr\u00f6ssen gab es einen signifikanten Zusammenhang. Die Effektsch\u00e4tzer nahmen jedoch unter Einbezug aller St\u00f6rfaktoren ab und waren nicht mehr signifikant.\r\nDie Inzidenz von Herz-/Kreislaufkrankheiten und Hirnschlag nahm jedoch bei Frauen mit Diabetes gegen\u00fcber Frauen ohne Diabetes f\u00fcr PM10, PM2.5-10 und PM2.5 zu mit HR 1.19 (95%CI: 1.1-1.28), 1.17 (1.05-1.3) und 1.44 (1.23-1.68), resp. 1.23 (1.1-1.38), 1.18 (1.01-1.38) und 1.66 (1.31-2.1). Die Belastung mit PM10 und PM2.5-10, nicht aber mit PM2.5 war ausserdem mit einem erh\u00f6hten Risiko bei Diabetikerinnnen f\u00fcr koronare Herzkrankheiten mit 1.12 (1.02-1.23) und 1.14 (1-1.3) verbunden.\r\nDie Autoren schliessen daraus, dass die Feinstaubbelastung bei Frauen mittleren Alters das Inzidenzrisiko f\u00fcr Herz-/Kreislaufkrankheiten gering, aber nicht signifikant erh\u00f6he, die Effektsch\u00e4tzer aber bei Frauen mit Diabetes erh\u00f6ht und signfikant waren.'), NULL, NULL, NULL, NULL, NULL, 4, TIMESTAMP '2016-12-14 14:47:29.436', 1, TIMESTAMP '2017-01-10 06:24:05.611', 2, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
