INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(10, '10.1136/bmjopen-2015-010004.', 27259522, STRINGDECODE('Oudin A, Br\u00e5b\u00e4ck L, \u00c5str\u00f6m DO, Str\u00f6mgren M, Forsberg B.'), 'Oudin', FALSE, 'Association between neighbourhood air pollution concentrations and dispensed medication for psychiatric disorders in a large longitudinal cohort of Swedish children and adolescents.', 'BMJ Open. 2016 Jun 3;6(6):e010004. DOI', 2016, STRINGDECODE('Registerbasierte Kohortenstudie in Schweden zur Untersuchung, ob die geistige Gesundheit mit der Schadstoffbelastung zusammenh\u00e4nge.'), STRINGDECODE('552''221 Kinder und Jugendliche, welche zwischen 2007 und 2010 in den Bezirken Stockholm, V\u00e4stra, G\u00f6taland, Skane und V\u00e4sterbotten wohnten, deren Eltern oder die Teilnehmer selbst zwischen 2005 und 2007 keine Medikamente der Gruppe N05 nach ATC gegen psychische Leiden bekommen haben.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Durch die Verkn\u00fcpfung der nationalen Gesundheitsdatenbank mit weiteren nationalen Registern wurden Informationen wie Alter, Geschlecht, Wohnort, Einkommen der Teilnehmer zusammengef\u00fchrt. Als Zielgr\u00f6sse wurde definiert, ob die Kinder und Jugendlichen im Untersuchungszeitraum mindestens einmal ein Medikament der ATC-Kategorie N05 bezogen hatten. In der Gruppe N05 sind Neuroleptika, Beruhigungsmittel und Schlafmittel, nicht aber Antidepressiva und Medikamente gegen Aufmerksamkeits- und Hyperaktivit\u00e4tsst\u00f6rungen (ADHS) enthalten. Die st\u00e4dtische Belastung mit Feinstaub PM2.5 und PM10 sowie NO2 an den geocodierten Adressen wurde mit einem Landnutzungsmodell zusammen mit einem Modell f\u00fcr die Hintergrundbelastung berechnet. Die durchschnittliche Jahresbelastung wurde den Teilnehmern f\u00fcr das Eintrittsjahr in die Studie zugeordnet.\r\nMit proportionalen Hazard-Modellen nach Cox wurde das Risiko f\u00fcr den Erhalt von Medikamenten der Gruppe N05 bis 2010 in Abh\u00e4ngigkeit der Luftbelastung an der Adresse untersucht, wobei das Geschlecht, Alter, Bildung der Eltern, BMI der Mutter bei Geburt, Rauchen w\u00e4hrend der Schwangerschaft, sozio\u00f6konomische Status auf Quartierebene und Einkommen der Eltern einbezogen wurden. Danach wurden geschichtete Analysen nach Geschlecht und Bezirk, Effektmodifikationen f\u00fcr Alter und sozio\u00f6konomische Status sowie verschiedene Sensitivit\u00e4tsanalysen zur \u00dcberpr\u00fcfung der Robustheit durchgef\u00fchrt. Ein Zweischadstoffmodell mit NO2 und PM10 wurde ebenfalls getestet. Mehrschadstoffmodell. Kohortenstudie. ZNS. Gehirn. Schweden.'), NULL, NULL, NULL, NULL, STRINGDECODE('Insgesamt 18''675 Teilnehmer erhielten mindestens einmal Medikamente der Gruppe N05. Die durchschnittliche Nachfolgezeit betrug 3.5 Jahre bei insgesamt \u00fcber 3.1 Mio. beobachteten Personenjahren. Im Durchschnitt betrug die Schadstoffbelastung 9.7 (Standardabweichung 7) \u00b5g NO2/m3 und 13.6 (5.9) \u00b5g PM10/m3. Die Schadstoffe waren hoch miteinander korreliert.\r\nDas Risiko der Kinder und Jugendlichen, ein Medikament der Gruppe N05 zu erhalten, war f\u00fcr einen Belastungsunterschied von 10 \u00b5g NO2/m3 mit HR 1.09 (95%-CI: 1.06-1.12) signifikant verbunden. Ebenfalls erh\u00f6hte Effektsch\u00e4tzer wurden bei einem Belastungsunterschied von 10 \u00b5g PM10/m3 mit 1.04 (1-1.08) berechnet. Dieser Zusammenhang war unabh\u00e4ngig vom Bezirk und dem Geschlecht. Eine Effektmodifikation durch das Alter oder den sozio\u00f6konomischen Status konnte nicht gefunden werden. In einem Zweischadstoffmodell wurden die Effektsch\u00e4tzer f\u00fcr die NO2-Belastung mit 1.18 (1.12-1.24) h\u00f6her und f\u00fcr die PM10-Belastung mit 0.9 (0.84-0.95) niedriger.\r\nDie Autoren folgern, dass m\u00f6glicherweise ein Zusammenhang zwischen der geistigen Gesundheit bei Kindern und Jugendlichen und der Luftschadstoffbelastung bestehe. Es seien allerdings weitere Studien notwendig, welche die gefundene Beziehung best\u00e4tigen k\u00f6nnen.'), NULL, NULL, STRINGDECODE('Die Resultate waren f\u00fcr PM10 und PM2.5 laut Autoren sehr \u00e4hnlich, weshalb nur Konzentrationen und Effektsch\u00e4tzer f\u00fcr PM10 angegeben wurden.'), NULL, '1F', 1, TIMESTAMP '2016-12-19 15:56:56.227', 4, TIMESTAMP '2016-12-19 15:56:56.227', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(11, '10.1016/j.dadm.2014.11.015.', 27239507, 'Wu YC, Lin YC, Yu HL, Chen JH, Chen TF, Sun Y, Wen LL, Yip PK, Chu YM, Chen YC.', 'Wu', FALSE, 'Association between air pollutants and dementia risk in the elderly.', 'Alzheimers Dement (Amst). 2015; 1 (2): 220-228.', 2015, STRINGDECODE('Fall-Kontrollstudie an \u00e4lteren Personen zur Untersuchung, ob es einen Zusammenhang zwischen Demenzerkrankungen und der Belastung mit Feinstaub oder Ozon gibt.'), STRINGDECODE('249 Alzheimerpatienten und 125 Patienten mit vaskul\u00e4rer Demenz aus drei neurologischen Kliniken in Taiwan zwischen 2007 und 2010, welche \u00e4lter als 60 Jahre alt waren und 497 gesunde Kontrollen ohne Drepressionen, Parkinson, h\u00e4moragische Schlaganf\u00e4lle, Hirninfarkte, Hirntumore und Demenz. Taiwan.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Mit einem Fragebogen wurden Informationen zu Demographie, Risikofaktoren f\u00fcr vaskul\u00e4re Erkrankungen, Lebensstil und Familienanamnese eingeholt. Eine Blutprobe wurde entnommen, um den Apolipoprotein E Haplotypen (APOE, Risikofaktor f\u00fcr Demenz) zu bestimmen. Vaskul\u00e4re Demenz und Alzheimer wurde durch spezialisierte Neurologen mit national anerkannten Tests bestimmt. Die Belastung mit Feinstaub PM10 und Ozon wurde mit Bayesscher Maximum Entropie Interpolation mit Daten von 24 Monitoren f\u00fcr die Gegend Taipei-Keelung zwischen 1993 und 2006 abgesch\u00e4tzt. Unter Annahme, dass \u00e4ltere Personen selten den Wohnort wechseln, wurde die Belastung mit PM10 \u00fcber 12, resp. die Ozonbelastung \u00fcber 14 Jahre den Teilnehmern f\u00fcr die Adresse bei Studieneintritt zugeordnet.\r\nMit bedingten logistischen Regressionen wurde der Zusammenhang der Alzheimer- und vaskul\u00e4ren Demenzdiagnose mit der Luftverschmutzung in Terzilen der Belastung untersucht.  Einbezogen wurde das Alter, Geschlecht, APOE Haplotyp, Alkoholkonsum, Bildung und BMI. Danach wurde die Analyse geschichtet nach Geschlecht und APOE-Typ wiederholt, um eine Effektmodifikation zu \u00fcberpr\u00fcfen. Mit dem Cochrane-Armitage-Test wurde eine Dosis-Wirkungsbeziehung zwischen den Luftschadstoffen und dem Demenzrisiko \u00fcberpr\u00fcft. Gehirn. ZNS. Fall-Kontrollstudie. Empfindliche Gruppen. Taiwan.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die Belastung im 1. Tertil betrug weniger als 44.95 \u00b5g PM10/m3 und 40.3 \u00b5g Ozon/m3, im 2. Tertil 44.95-49.23 \u00b5g PM10/m3 und 40.3-43 \u00b5g O3/m3 und im 3. Tertil mehr als 49.23 \u00b5g PM10/m3 und 43 \u00b5g Ozon/m3.\r\nIm Vergleich mit den Kontrollen, waren die Alzheimerpatienten \u00e4lter, weniger gebildet, APOE-Epsilon 4 Typen und weiblich, hatten einen h\u00f6heren BMI, eher Diabetes, weniger Bluthochdruck und Hyperlipid\u00e4mie. Die Teilnehmer, welche an vaskul\u00e4rer Demenz erkrankt waren, waren gegen\u00fcber den Kontrollen weniger gebildet, \u00e4lter, konsumierten mehr Alkohol, rauchten mehr und hatten eher Diabetes und Bluthochdruck.\r\nTeilnehmer im h\u00f6chsten Belastungstertil von PM10 und Ozon hatten ein um 4.2% (95%-CI: 2.31-7.54), resp. 2% (1.1-3.5) signifikant erh\u00f6htes Risiko an Alzheimer zu erkranken, gegen\u00fcber Teilnehmern im niedrigsten Tertil. Ein erh\u00f6htes Risiko um 3.61% (1.7-7.8), resp. 2.1% (1-4.3) an vaskul\u00e4rer Demenz zu leiden hing ebenfalls signifikant mit der PM10-, resp. Ozon-Belastung zusammen. Die Dosis-Wirkungsbeziehung zwischen Alzheimer und vaskul\u00e4rer Demenz und der PM10- und Ozonbelastung war linear.\r\nWeder f\u00fcr den APOE-E4-Typen, noch das Geschlecht konnte eine Effektmodifikation der Beziehung zwischen Alzheimer und vaskul\u00e4rer Demenz und der PM10- und Ozonbelastung gefunden werden.\r\nDie Autoren folgern, dass neurodegenerative Erkrankungen wie Alzheimer und vaskul\u00e4re Demenz m\u00f6glicherweise durch die Luftschadstoffbelastung beeinflusst werden.'), NULL, NULL, STRINGDECODE('Aus dem Artikel geht nicht hervor, nach welchen Kriterien die Kontrollen ausgew\u00e4hlt wurden.'), NULL, NULL, 1, TIMESTAMP '2016-12-19 15:59:57.187', 4, TIMESTAMP '2016-12-19 15:59:57.187', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(12, NULL, NULL, 'Tzivian L, Winkler A, Dlugaj M, Schikowski T, Vossoughi M, Fuks K, Weinmayr G, Hoffmann B.', 'Tzivian', FALSE, 'Effect of long-term outdoor air pollution and noise on cognitive and psychological functions in adults.', 'Int J Hyg Environ Health. 2015; 218 (1): 1-11.', 2016, STRINGDECODE('Narrative \u00dcbersicht \u00fcber Studien, welche die Beziehung zwischen der geistigen Gesundheit bei Erwachsenen und der Luftverschmutzung und L\u00e4rm untersucht haben.'), STRINGDECODE('22 epidemiologische Studien in Englisch, gesucht in PubMed und Google Scholar bis Ende November 2013 mit Stichworten zur kognitiven Funktion, neurologischen Erkrankungen, Gem\u00fctsst\u00f6rungen, Luftverschmutzung und L\u00e4rm. Es wurden nur Langzeitstudien mit einer Belastung von mindestens einem Jahr und erwachsenen (>18Jahre) Teilnehmern eingeschlossen. Deutschland.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('ZNS. Gehirn. Kognitive F\u00e4higkeit. \u00dcbersicht. Wirkungsmechanismen. Feinstaub. NO2. Ozon. Kohlenmonoxid CO. SO2. Verkehr. Black Carbon BC. Russ. Feinstaub. PM2.5. PM10. Partikel. Luftqualit\u00e4tsindex. L\u00e4rm. Epidemiologische Untersuchungen an Erwachsenen. neurologische Entwicklung. Hirnleistung. Exekutive Funktion (EF). Ged\u00e4chtnis. visuelle motorische F\u00e4higkeit. Aufmerksamkeit. Parkinson. Alzheimer. Demenz. Deutschland.'), NULL, NULL, NULL, NULL, STRINGDECODE('14 der 22 gefunden Studien untersuchten Zusammenh\u00e4nge zwischen der geistigen Gesundheit und der Luftverschmutzung. Darunter waren 9 Querschnitts-, 3 Kohorten- und 2 Fall-Kontrollstudien. Von den 8 Studien, welchen die Beziehung zum L\u00e4rm untersuchten, w\u00e4hlten 4 das Querschnitts-, und jeweils 2 das Fall-Kontroll- und Kohortenstudiendesign. Ein direkter Vergleich zwischen den berechneten Effekten in Abh\u00e4ngigkeit der Luftbelastung und des L\u00e4rms war aufgrund der unterschiedlichen Methoden zur Messung der kognitiven Funktion nicht m\u00f6glich. Nur eine einzige Studie hat beide Belastungen gemessen, eine Interaktion untereinander jedoch nicht untersucht. Doch auch ein Vergleich unter den Studien, welche nur einen Zusammenhang zwischen der geistigen Gesundheit und L\u00e4rm oder der Luftbelastung untersucht haben, war aufgrund der vielen verschiedenen Zielgr\u00f6ssen nicht m\u00f6glich, weshalb die Autoren kein systematisches Review oder eine Metaanalyse durchf\u00fchren konnten. Dennoch wurde in allen Studien mindestens ein Indikator f\u00fcr die geistige Gesundheit gefunden, welcher in Abh\u00e4ngigkeit einer der Belastungen beeintr\u00e4chtigt wurde. Als m\u00f6gliche Wirkungsmechanismen wurde einerseits die direkte Aufnahme von Luftschadstoffen \u00fcber die Lunge genannt, welche zu eine lokalen Entz\u00fcndung f\u00fchren k\u00f6nnen, worauf der K\u00f6rper entz\u00fcndungsf\u00f6rderne Zytokine freisetze, welche die Blut-Hirnschranke \u00fcberwinden k\u00f6nnen. Andererseits g\u00e4be es Hinweise darauf, dass Luftschadstoffe \u00fcber den Riechnerv direkt ins Hirn gelangen k\u00f6nnen. L\u00e4rm wiederum wirke m\u00f6glicherweise \u00fcber stressinduzierte Signalwege, etwa \u00fcber eine Aussch\u00fcttung von Noradrenalin und Dopamin im Hypothalamus, was die Regulation des pr\u00e4frontalen Kortex (zust\u00e4ndig f\u00fcr kognitive F\u00e4higkeiten) beeintr\u00e4chtigen k\u00f6nne.\r\nDie Autoren folgern deshalb, dass m\u00f6glicherweise ein Zusammenhang zwischen der kognitiven Funktion, neurologischen Erkrankungen und Gem\u00fctsst\u00f6rungen und der Luftbelastung und L\u00e4rm bestehe. Die Evidenz daf\u00fcr m\u00fcsse jedoch durch weitere Langzeitstudien, welche die Entwicklung der kognitiven Funktion \u00fcber die Zeit untersuchen und sowohl die Luftbelastung als auch die L\u00e4rmbelastung erheben, mit vergleichbaren Zielgr\u00f6ssen untermauert werden.'), NULL, NULL, NULL, NULL, '1F', 1, TIMESTAMP '2016-12-19 16:02:33.36', 4, TIMESTAMP '2016-12-19 16:02:33.36', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(13, '10.1371/journal.pone.0161851.', 27653281, 'Lam J, Sutton P, Kalkbrenner A, Windham G, Halladay A, Koustas E, Lawler C, Davidson L, Daniels N, Newschaffer C, Woodruff T.', 'Lam', FALSE, 'A Systematic Review and Meta-Analysis of Multiple Airborne Pollutants and Autism Spectrum Disorder.', 'PLoS One. 2016; 11 (9): e0161851. DOI', 2016, STRINGDECODE('Systematische \u00dcbersicht und Meta-Analyse zur Untersuchung, ob Autismus mit der Aussen- und Innenluftverschmutzung zusammenh\u00e4nge.'), STRINGDECODE('17 Fall-Kontroll-, 4 \u00f6kologische und 2 Kohortenstudien, welche in PubMed, Web of Science, Biosis Previews, Embase, Google Scholar und Toxline ohne Spracheinschr\u00e4nkung bis zum 5.11.2014 gefunden wurden, erg\u00e4nzt mit Handsuche und Referenzenlisten, sowie eine Befragung von Experten bis M\u00e4rz 2015. USA.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Entsprechend der Richtlinien des Navigation Guide, welcher sich an den Methoden von Cochrane und GRADE orientiert, wurde die Studienauswahl organisiert und die wissenschaftliche Evidenz und Qualit\u00e4t der Studien von mehreren Experten im Bereich Luftverschmutzung und Autismus evaluiert und im Plenum diskutiert. Die Studien wurden aufgeschl\u00fcsselt nach Autor und Publikationsjahr, Studiendesign, Ort, Anzahl Teilnehmer, Belastungsabsch\u00e4tzung und Zielgr\u00f6sse. \r\nAufgrund der ungen\u00fcgenden Anzahl Studien oder zu grosser Heterogenit\u00e4t konnte eine Metaanalyse nur f\u00fcr Feinstaub PM10 und PM2.5 durchgef\u00fchrt werden. Die Effektsch\u00e4tzer wurden auf 10 \u00b5g/m3 standardisiert. Mit Modellen f\u00fcr zuf\u00e4llig verteilte Effekte wurden die Effektsch\u00e4tzer metaanalytisch kombiniert. Mit Strukturentdeckungen mittels hierarchischen Clusteranalysen wurden die Analysen wiederholt, um die Heterogenit\u00e4t zu ber\u00fccksichtigen. Mit Sensitivit\u00e4tsanalysen wurde abgesch\u00e4tzt, ob durch das Erscheinen weiterer Studien die Richtung eines Zusammenhangs ver\u00e4ndert werden kann. Systematische \u00dcbersicht. Kinder. Autismus. kognitive Entwicklung. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('Das Risiko f\u00fcr Verzerrung wurde in den meisten Bereichen der Studien als gering oder wahrscheinlich gering eingestuft. Einige Studien wurden im Bereich der einbezogenen St\u00f6rgr\u00f6ssen oder in der Absch\u00e4tzung der Belastung als eventuell verzerrt eingestuft. Insbesondere Studien, welche die Belastung mit dem amerikanischen NATA-Programm abgesch\u00e4tzt hatten, waren davon betroffen.\r\nDie 6, resp. 3 Studien f\u00fcr PM10 und PM2.5, welche in der Metaanalyse gepoolt wurden, hatten mehrheitlich das Alter der Mutter, die elterliche Bildung, Rasse, Geschlecht des Kindes, Haushaltseinkommen und verschiedene Indikatoren des SES miteinbezogen. Das gepoolte Risiko f\u00fcr Autismus bei einem Anstieg von 10 \u00b5g/m3 PM10, resp. PM2.5 betrug OR 1.2 (95%-CI: 1-1.42) und OR 1.88 (1.11-3.2). Die Heterogenit\u00e4t war mit 77%, resp. 96% (I-Quadrat) sehr hoch. Mit Kreuzvalidierungsverfahren wurde jeweils eine Studie identifiziert, die haupts\u00e4chlich f\u00fcr die Heterogenit\u00e4t verantwortlich war. Deshalb wurde die Analyse unter Einbezug von hierarchischen Clusteranalysen wiederholt, was zu einer deutlich geringeren, unerkl\u00e4rbaren Heterogenit\u00e4t f\u00fchrte. Das gepoolte Risiko f\u00fcr Autismus betrug nun f\u00fcr PM10 und PM2.5 1.07 (1.06-1.08) und 2.32 (2.15-2.51).\r\nNach der Sensitivit\u00e4tsanalyse wurde es f\u00fcr die Autoren unwahrscheinlich, wenn auch nicht unm\u00f6glich, dass eine weitere Studie die Effektsch\u00e4tzer umkehren w\u00fcrde. Und obwohl es noch zu wenig Studien gebe und deshalb auch keine \u00dcberpr\u00fcfung der Verzerrung mittels Trichtergrafik m\u00f6glich war, folgern die Autoren, dass Hinweise f\u00fcr einen Zusammenhang zwischen Autismus und der Luftbelastung vor und nach der Geburt derzeit beschr\u00e4nkt ist.'), NULL, NULL, STRINGDECODE('Die Autoren sind sich bewusst, dass mehrere Studien, darunter Kohorten, nach dem Suchzeitraum publiziert wurden. Einen Teil dieser Studien wurde im systematischen Review von Flores et al. (ID 8743) ber\u00fccksichtigt, weshalb dort die Evidenz f\u00fcr einen Zusammenhang bereits als \u00fcberzeugender dargestellt wird.'), NULL, '1F', 1, TIMESTAMP '2016-12-19 16:05:05.335', 4, TIMESTAMP '2016-12-19 16:05:05.335', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(14, '10.1186/s12940-015-0064-1.', 26444407, 'Talbott EO, Marshall LP, Rager JR, Arena VC, Sharma RK, Stacy SL.', 'Talbott', FALSE, 'Air toxics and the risk of autism spectrum disorder: the results of a population based case-control study in southwestern Pennsylvania.', 'Environ Health. 2015; 14: 80.', 2015, STRINGDECODE('Fall-Kontrollstudie in den USA zur Untersuchung, ob die M\u00fctter von Kindern mit Autismus w\u00e4hrend der Schwangerschaft und die Kinder bis zum Alter von 2 Jahren st\u00e4rker mit Luftschadstoffen belastet waren als vergleichbare Familien.'), STRINGDECODE('217 Kinder mit Autismus und 224 Kontrollkinder, passend im Geburtsjahr, Geschlecht und Rasse, aus dem S\u00fcdwesten von Pennsylvania, welche zwischen dem 01.01.2005 und 31.12.2009 geboren wurden sowie 4971 Kontrollkinder, geboren im selben Zeitraum, aus dem Geburtsregister. USA.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Kinder der Geburtsjahre 2005-2009 mit der Diagnose Autismus wurden aktiv gesucht: in Kliniken und Ambulatorien f\u00fcr Autismus, bei Kinder- und Haus\u00e4rzten und in schulischen Einrichtungen f\u00fcr Kinder mit Entwicklungsst\u00f6rungen. Die Kinder wurden eingeschlossen, wenn sie mindestens 15 Punkte im SCQ-Fragebogen (Social Communication Questionnaire) f\u00fcr das Vorhandensein von autistischen Verhaltensweisen erreichten oder eine psychiatrische oder psychologische Best\u00e4tigung einer Autismus-Diagnose vorlag. Die Kontrollkinder stammten aus dem Geburtenregister der gleichen Region (6 Kantone des s\u00fcdwestlichen Pennsylvania). Mit allen M\u00fcttern der der Fall-Kontrollstudie wurde ein Interview (in Anlehnung an die CDC-Studie zur fr\u00fchkindlichen Entwicklung) zur demographischen und sozio\u00f6konomischen Situation der Eltern, Familiengeschichte des Autismus, Raucherstatus, Schwangerschaft(en), Medikamenteneinnahme des Kindes und Wohnorten w\u00e4hrend der Schwangerschaft bis zum Kindesalter von 2 Jahren durchgef\u00fchrt. Eine zweite Kontrollgruppe von Kindern wurde zufallsbedingt aus dem Geburtenregister ausgew\u00e4hlt, dem Informationen zum Alter der Mutter, Rauchen, Bildung, Rasse entnommen wurden.\r\nMit dem NATA-Programm, das die Konzentrationen von 37 Stoffen aus vielen lokalen Emissionen errechnete, f\u00fcr welche eine Toxizit\u00e4t f\u00fcr die neurologische Entwicklung beim Menschen vermutet wird, wurde die Belastung im Quartier (Volksz\u00e4hlungseinheit) den geocodierten oder gem\u00e4ss Postleitzahl lokalisierten Adressen bestimmt. Die modellierte Konzentrationen von 30 chemischen Komponenten im Jahr 2005 wurden allen Kindern w\u00e4hrend der Schwangerschaft und im 1. und 2. Lebensjahr zugeordnet und in Belastungsquartile unterteilt.\r\nMit logistischen Regressionsanalysen wurde der Zusammenhang zwischen Autismus und der modellierten Luftbelastung f\u00fcr die verschiedenen Zeitfenster unter Einbezug von Geburtsjahr, Geschlecht, Rasse, Rauchen, Alter und Bildung der Mutter untersucht. Danach wurden die 30 Schadstoffe in drei Gruppen (Metalle ohne Selen, aromatische L\u00f6sungsmittel und chlorierte L\u00f6sungsmittel) unterteilt und die Analyse f\u00fcr diese 3 Gruppen wiederholt. Sensitivit\u00e4tsanalysen, bspw. unter Ausschluss von Mehrfachgeburten, wurden durchgef\u00fchrt. Als letztes wurde eine schrittweise r\u00fcckw\u00e4rtsgerichtete logistische Regression f\u00fcr die zuvor als wichtig bestimmten Schadstoffkomponenten durchgef\u00fchrt. ZNS. Gehirn. Neuromotorische Entwicklung. Kognitive Entwicklung. polyzyklische aromatische Wasserstoffe PAK. Diesel. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die Interquartilwerte der Belastung betrugen f\u00fcr die F\u00e4lle, resp. Kontrollen 0.81 / 0.52 ng Chrom/m3, 0.27 / 0.2 ng Nickel/m3, 0.91 / 0.96 ng Blei/m3, 26.17 / 17.52 ng Styrol/m3, 7.02 / 5.95 ng PAK/m3 und 369.03 / 334.33 ng Diesel PM/m3. M\u00fctter aus der telefonischen Befragung der h\u00f6chsten Styrolbelastung w\u00e4hrend der Schwangerschaft (4. Quartil) hatten ein um 2.04 (95%-CI: 1.17-3.58) h\u00f6heres Risiko, ein Kind mit Autismus zu geb\u00e4ren, gegen\u00fcber M\u00fcttern der niedrigsten Belastung. Das Risiko f\u00fcr die Belastung w\u00e4hrend dem ersten Jahr war mit 1.86 (1.07-3.25) noch signifikant erh\u00f6ht, f\u00fcr die Belastung im zweiten Jahr nur noch erh\u00f6ht, aber nicht signifikant. Das Risiko war ausserdem paradox signifikant erniedrigt f\u00fcr das 3. Belastungsquartil von Nickel gegen\u00fcber dem 1. mit 0.49 (0.27-0.89). F\u00fcr alle anderen Schadstoffkomponenten konnte kein Zusammenhang mit Autismus gefunden werden. Im Vergleich mit der Kontrollgruppe aus den randomisierten Geburtszertifikaten war das Risiko f\u00fcr M\u00fctter im 4. Belastungsquartil von Styrol und Chrom gegen\u00fcber dem 1. Quartil w\u00e4hrend der Schwangerschaft signifikant erh\u00f6ht mit 1.61 (1.08-2.4) und 1.6 (1.08-2.38). Ausserdem war das 2. Belastungsquartil von Styrol und das 3. Quartil der Bleibelastung signifikant mit einem erh\u00f6hten Risiko, ein Kind mit Autismus zu geb\u00e4ren erh\u00f6ht. Keine der drei Schadstoffgruppen war mit der Geburt von Kindern mit Autismus verbunden. Wurden nur Einlingsgeburten in die Analyse einbezogen, war das Risiko f\u00fcr die F\u00e4lle und die Kontrollgruppe aus den Geburtenregistern f\u00fcr das 4. gegen\u00fcber dem 1. Belastungsquartil f\u00fcr Arsen, Chrom, Methylenchlorid, polyzyklische aromatische Wasserstoffe PAK, Styrol, Blei und Zyanid erh\u00f6ht. In der r\u00fcckw\u00e4rtsgerichteten Regressionsanalyse blieb aber lediglich das Risiko f\u00fcr eine Belastung mit Styrol mit 2.04 (1.17-3.58) f\u00fcr die Interviewkontrolle und 1.61 (1.08-2.4) f\u00fcr die Geburtenregister-Kontrolle f\u00fcr das 4. gegen\u00fcber dem 1. Belastungsquartil.\r\nDie Autoren folgern, dass das Risiko, ein Kind mit Autismus zu geb\u00e4ren, verbunden sei mit der Styrol- und Chrombelastung und eventuell auch mit der PAK- und Methylenchloridbelastung.'), NULL, NULL, NULL, NULL, '1F', 1, TIMESTAMP '2016-12-19 16:12:31.69', 4, TIMESTAMP '2016-12-19 16:12:31.69', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(15, '10.1097/FCH.0000000000000112.', 27214671, 'Clark-Reyna SE, Grineski SE, Collins TW.', 'Clark-Reyna', FALSE, 'Health Status and Residential Exposure to Air Toxics: What Are the Effects on Children''s Academic Achievement?', 'Fam Community Health. 2016; 39 (3): 160-168.', 2016, STRINGDECODE('Querschnittsstudie in den USA zur Untersuchung, ob schulische Leistung mit der Feinstaubbelastung am Wohnort und mit dem allgemeinen Gesundheitszustand zusammenh\u00e4ngen.'), STRINGDECODE('1895 Kinder der 4. und 5. Primarklasse aller \u00f6ffentlichen Schulen in El Paso Texas, deren Eltern einen Fragebogen komplett ausgef\u00fcllt hatten. USA.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Im Jahr 2012 wurde allen Eltern von Kindern in der 4. und 5. Klasse ein englischer oder spanischer Fragebogen zugestellt. Darin wurden sie nach den Notendurchschnitten (A-F) in allen sechs F\u00e4chern, sowie dem Gesundheitszustand (von 1 f\u00fcr sehr schlecht bis 6 f\u00fcr exzellent) der Kinder gefragt. Die Noten wurden in Zahlen von 0 f\u00fcr F bis 4 f\u00fcr A umgewandelt und addiert um im Anschluss den Durchschnitt \u00fcber alle F\u00e4cher zu berechnen. Die Adressen der Teilnehmer wurden geocodiert und den Quartieren (Volksz\u00e4hlungseinheiten) zugeordnet. F\u00fcr diese wurde mit dem National-Scale Air Toxics Programm (NATA) der US-EPA die Belastung mit Feinstaub im Jahr 2005 ermittelt. Die Partikel des Feinstaubs wurden dabei nach Art des vermuteten Gesundheitsrisikos unterteilt, summiert in totales respiratorisches Risiko (alle respiratorischen Gesundheitseffekte) und diesel-verursachtes Risiko (respiratorische und kardiovaskul\u00e4re, nicht kanzerogene Gesundheitseffekte).\r\nAls erstes wurde mit verallgemeinerten Sch\u00e4tzgleichungen die Beziehung zwischen dem Notendurchschnitt und dem allgemeinen Gesundheitszustand untersucht. Danach wurde ein Zusammenhang zwischen der schulischen Leistung und den Luftschadstoffen analysiert. In einem letzten Schritt wurde wurde eine m\u00f6gliche Interaktion der Luftschadstoffe auf die Beziehung zwischen dem Notendurchschnitt und dem allgemeinen Gesundheitszustand untersucht. In allen Modellen wurde das Alter, Geschlecht, Rasse, m\u00fctterliche Bildung und Englischkenntnisse, Alter der Mutter (Erwachsen oder Teenager), Bezug von erm\u00e4ssigtem Mittagessen (Armutsindikator) und die Schule einbezogen. Sensitivit\u00e4tsanalysen zur \u00dcberpr\u00fcfung der Robustheit wurden durchgef\u00fchrt. Querschnittstudie. Kognitive Entwicklung. ZNS. Gehirn. Intelligenz. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('73% der Eltern sagten aus, dass sich ihr Kind in exzellentem oder sehr gutem Gesundheitszustand und 21% in gutem Zustand befinde. Der Notendurchschnitt betrug 3.3 und 78% der Kinder hatten einen Schnitt von mehr als 3. Die Luftschadstoffe waren signifikant invers mit dem Notendurchschnitt der Kinder sowie dem allgemeinen Gesundheitszustand korreliert.\r\nEin besserer allgemeiner Gesundheitszustand der Kinder laut Eltern war signifikant mit einem besseren Notendurchschnitt verbunden. Knaben im Allgemeinen und Kinder, welche erm\u00e4ssigte Mittagessen erhielten, hatten signifikant niedrigere Notendurchschnitte. Umgekehrt schnitten Kinder von M\u00fcttern mit h\u00f6herem Bildungsniveau und besseren Englischkenntnissen besser ab. Unter Einbezug der Feinstaubbelastung blieb der Effektsch\u00e4tzer des Gesundheitszustandes unver\u00e4ndert erhalten. Mit zunehmender Feinstaubbelastung, sowohl f\u00fcr Partikel mit einem totalen respiratorischen Risiko, als auch mit einem diesel-verursachten Risiko, nahm der Notendurchschnitt der Kinder signifikant ab. Eine Interaktion der Schadstoffe mit der Beziehung zwischen dem Gesundheitszustand und dem Notendurchschnitt konnte nicht gefunden werden.\r\nDie Autoren folgern, dass die schulische Leistung im Primarschulalter in Abh\u00e4ngigkeit der Luftverschmutzung beeintr\u00e4chtigt sein k\u00f6nnte, unabh\u00e4ngig vom subjektiven Gesundheitszustandes des Kindes.'), NULL, NULL, NULL, NULL, '1F', 1, TIMESTAMP '2016-12-19 16:14:26.196', 4, TIMESTAMP '2016-12-19 16:14:26.196', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(16, '10.1111/jcpp.12548.', 26989990, 'Margolis AE, Herbstman JB, Davis KS, Thomas VK, Tang D, Wang Y, Wang S, Perera FP, Peterson BS, Rauh VA.', 'Margolis', FALSE, 'Longitudinal effects of prenatal exposure to air pollutants on self-regulatory capacities and social competence.', 'J Child Psychol Psychiatry. 2016; 57 (7): 851-860.', 2016, STRINGDECODE('S\u00e4uglingskohortenstudie mit Familien in New York zur Untersuchung, ob Verhaltensprobleme und eine ung\u00fcnstige Entwicklung der sozialen Kompetenz des Kindes im Alter von drei bis 11 Jahren mit der Belastung mit polyzyklischen aromatischen Kohlenwasserstoffen in der Schwangerschaft verbunden sei.'), STRINGDECODE('462 Kinder von Frauen dominikanischer und schwarzamerikanischer Herkunft aus New York (NYC-Studie des Columbia Center for Children''s Environmental Health CCCEH), alle nicht rauchend, 18-35 Jahre alt, ohne Diabetes, hohen Blutdruck oder HIV, welche bereits in der 20. Schwangerschaftswoche f\u00fcr diese Kohortenstudie rekrutiert worden waren. USA.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Die M\u00fctter wurden im zweiten Trimester rekrutiert und nach der Anamnese, Passivrauchen, Ern\u00e4hrung (Konsum von ger\u00e4ucherter, gebratener, grillierter Nahrung) gefragt. Innert einem Tag nach der Geburt wurde der Mutter eine Blutprobe entnommen. Darin wurden die Benzo(a)pyren-Addukte an der DNS der weissen Blutk\u00f6rperchen (HPLC-Fluoreszenzmethode) analysiert. Die M\u00fctter wurden j\u00e4hrlich mit einer spezialisierten Befragung (PERI-Demoralization) auf psychologischen Stress untersucht. Im Alter des Kindes von 3-5, 7, 9 und 11 Jahren f\u00fcllten die M\u00fctter einen Verhaltensfragebogen f\u00fcr Kinder (CBCL-Test mit 118 Fragen) aus, welcher zur Vergleichbarkeit auf einen Durchschnitt von 50 Punkten standardisiert wurde. Die soziale Kompetenz der Kinder wurde im Alter von 11 Jahren mit dem SRS-Test durch die M\u00fctter bestimmt.\r\nDie polyzyklischen aromatischen Kohlenwasserstoff-Addukte (PAK) an der DNS der Leukozyten im m\u00fctterlichen Blut bei Geburt wurden als vorhanden oder nicht vorhanden klassifiziert. Die Beziehung zwischen dem bin\u00e4ren PAK-Indikator und dem Verhalten und der sozialen Kompetenz des Kindes wurde mit verallgemeinerten Sch\u00e4tzgleichungen untersucht. Danach wurden Zusammenh\u00e4nge zwischen dem Verhalten im Alter von 9 Jahren und der sozialen Kompetenz mit 11 Jahren analysiert. Ausserdem wurde mit dem Sobel-Test (Mediation) \u00fcberpr\u00fcft, ob das Verhalten die soziale Kompetenz beeinflusst. Einbezogen wurde das Geschlecht, Rasse, Bildung, Zivilstand und Stress der Mutter. Geburtskohortenstudie. Gehirn. ZNS. kognitive Entwicklung. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('Im Blut von 40% der M\u00fctter konnten PAK-Addukte nachgewiesen werden.\r\nIm Alter von 3 Jahren, hatten alle Kinder eine vergleichbare Punktezahl beim Verhaltenstest unabh\u00e4ngig von der PAK-Belastung im m\u00fctterlichen Blut. Im Alter von 9 und 11 Jahren hatten Kinder von M\u00fcttern mit PAK-Belastung im Blut jedoch einen signifikant h\u00f6heren (schlechteren) Punktestand gegen\u00fcber den anderen Kindern. Um auszuschliessen, dass lediglich ADHS-Symptome und nicht selbstreguliertes Verhalten gemessen wurden, wurden die einzelnen Kategorien des Verhaltenstests (\u00c4ngstlichkeit/Depression, Agressivit\u00e4t und Aufmerksamkeit) mit der PAK-Belastung in Beziehung gesetzt. Auch die 3 Unterkategorien waren signifikant mit den PAK-Addukten im Blut verbunden. Die soziale Kompetenz im Alter von 11 Jahren war mit der PAK-Belastung und dem Verhalten im Alter von 9 Jahren signifikant verbunden. Der Mediationstest nach Sobel zeigte ausserdem, dass die Beziehung zwischen der sozialen Kompetenz und der PAK-Belastung im m\u00fctterlichen Blut durch das Verhalten vermittelt wird.\r\nDie Autoren folgern, dass das die pr\u00e4natale PAK-Belastung das Verhalten und die soziale Kompetenz beeintr\u00e4chtigen und damit zu idiopathischen Kinderkrankheiten beitragen k\u00f6nne.'), NULL, NULL, NULL, NULL, '1C', 1, TIMESTAMP '2016-12-19 16:16:16.157', 4, TIMESTAMP '2016-12-19 16:16:16.157', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(17, '10.1289/EHP209', 27128166, STRINGDECODE('Basaga\u00f1a X, Esnaola M, Rivas I, Amato F, Alvarez-Pedrerol M, Forns J, L\u00f3pez-Vicente M, Pujol J, Nieuwenhuijsen M, Querol X, Sunyer J.'), STRINGDECODE('Basaga\u00f1a'), FALSE, 'Neurodevelopmental Deceleration by Urban Fine Particles from Different Emission Sources: A Longitudinal Observational Study.', 'Environ Health Perspect. 2016; 124 (10): 1630-1636.', 2016, STRINGDECODE('Kohortenstudie an spanischen Schulkindern zur Untersuchung, ob die Entwicklung der Hirnleistung durch die verschiedenen Quellen des Feinstaubs beeintr\u00e4chtigt wird.'), STRINGDECODE('2618 Schulkinder im Alter von 7 bis 11 Jahren (Durchschnitt 8.5 Jahre) aus 39 Schulen in Barcelona, welche gepaart nach hoch- bzw. tief mit NO2 belastetet, privat bzw. \u00f6ffentlich und Sozialstatus ausgew\u00e4hlt wurden. Die Sch\u00fcler waren seit mindestens 6 Monaten in derselben Schule eingeschrieben. BREATHE Projekt Spanien.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Die Hirnleistung der Schulkinder wurden zwischen Januar 2012 und M\u00e4rz 2013 alle 3 Monate insgesamt vier Mal untersucht. Computergest\u00fctzt wurden das Arbeitsged\u00e4chtnis, das h\u00f6here Arbeitsged\u00e4chtnis und die Aufmerksamkeit gemessen. Zur Anwendung kamen der sog. N-Back-Test (2- und 3-back), bei dem vorgegebene Farben, Buchstaben oder Zahlen in einer Reihe richtig erkannt werden m\u00fcssen, und der Attentional Network Test, bei dem Pfeilrichtungen m\u00f6glichst schnell und richtig erkannt werden m\u00fcssen und als Standardfehler der Reaktionszeit f\u00fcr richtige Antworten (HRT-SE) gemessen wurden. Diese Hirnleistungstests zeigen eine altersabh\u00e4ngige Entwicklung. Die Feinstaubbelastung PM2.5 wurde mit Messkampagnen im gleichen Zeitraum 2012/2013 zeitgleich in den gepaarten Schulen \u00fcber einen Zeitraum von einer Woche in der warmen und der kalten Jahreszeit in einem Klassenzimmer und auf dem Schulhof durchgef\u00fchrt. Die Kombination der durchschnittlichen Schadstoffbelastungen w\u00e4hrend der beiden Messwochen ergaben unter Einbezug von saisonalen Schwankungen (Abgleich mit offiziellen Messdaten) die langfristigen Belastungen in den Schulen. Aus den Filtern der Messkampagne wurden 33 chemische Komponenten mit Massenspektromie bestimmt. Mit positiver Matrixfaktorisierung wurden die Feinstaubbestandteile dann neun Quellen zugeordnet: Mineralstoffe (st\u00e4dtischer Staub, Baustellen), Verkehr, organische Stoffe/Textilverarbeitung/Kalkfarbe, sekund\u00e4re Sulfate / organische Stoffe (Kraftwerke, Schiffsdiesel, verfrachtete Stoffe), sekund\u00e4re Nitrate (mobile Quellen und andere Verbrennungsprozesse), Strassenstaub (Bremsen-, Reifen-, Belagsabrieb, Aufwirbelung), Metallemissionen, Meersalz (Gischt) und Schwer\u00f6lverbrennung. Die Feinstaubbelastung an der Wohnadresse wurde mit Landnutzungsmodellen nach der ESCAPE-Methode berechnet.'), NULL, NULL, NULL, NULL, STRINGDECODE('Im Beobachtungszeitraum von einem Jahr verbesserte sich das Arbeitsged\u00e4chtnis durchschnittlich um 13% und das h\u00f6here Arbeitsged\u00e4chtnis um 16.5% und die Unaufmerksamkeit sank um 14%. Die Belastung an den Schulen betrug median in der Aussenluft 28 (Interquartilabstand IQR 13.2) \u00b5g PM2.5/m3 und in der Raumluft 36 (12.3) \u00b5g PM2.5/m3. Den gr\u00f6ssten Anteil der Feinstaubbelastung in der Aussenluft lieferten mineralische Stoffe (27%), gefolgt von Verkehr (17%), organische Stoffe/Textilverarbeitung/Kalkfarbe (16%), sekund\u00e4re Sulfate / organische Stoffe (14%) und sekund\u00e4re Nitrate (13). Die Innenluftbelastung wurde durch organische Stoffe/Textilverarbeitung/Kalkfarbe als Quelle mit 45% dominiert, ansonsten war die Verteilung in gleicher Reihenfolge.\r\nEin schlechteres Abschneiden beim Arbeitsged\u00e4chtnis, beim h\u00f6heren Arbeitsged\u00e4chtnis und der Aufmerksamkeit um 5.6 (95%-CI: 0.5-10.7), 5.1 (1.1-9.2) und 3.6 (0-7.1) Punkte war mit der Feinstaubbelastung aus dem Verkehr in der Raumluft pro IQR-Unterschied signifikant verbunden. Dies entsprach 22%, 30% und 11% der j\u00e4hrlichen Ver\u00e4nderung der drei Zielgr\u00f6ssen. Die Effektsch\u00e4tzer f\u00fcr die Beziehung zur Aussenluft waren \u00e4hnlich, aber geringer. Das h\u00f6here Arbeitsged\u00e4chtnis nahm ausserdem pro IQR-Anstieg von Feinstaub aus mineralischen Bestandteilen um 4.1 (0.8-7.4) Punkte ab. Alle anderen Feinstaubquellen konnten nicht mit den Zielgr\u00f6ssen in Verbindung gebracht werden. Elementarer Kohlenstoff als Bestandteil des Feinstaubs war mit allen drei Zielgr\u00f6ssen verbunden, w\u00e4hrend Cu und Sb lediglich mit der Aufmerksamkeit verbunden waren.\r\nDie Autoren folgern, dass die Feinstaubbelastung aus dem Verkehr die kognitive Entwicklung von Kindern im Primarschulalter beintr\u00e4chtigen k\u00f6nnte.'), NULL, NULL, NULL, NULL, '1F', 1, TIMESTAMP '2016-12-19 16:24:16.881', 4, TIMESTAMP '2016-12-19 16:24:16.881', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(18, '10.1289/ehp.1409449.', 26241036, STRINGDECODE('Forns J, Dadvand P, Foraster M, Alvarez-Pedrerol M, Rivas I, L\u00f3pez-Vicente M, Suades-Gonzalez E, Garcia-Esteban R, Esnaola M, Cirach M, Grellier J, Basaga\u00f1a X, Querol X, Guxens M, Nieuwenhuijsen MJ, Sunyer J.'), 'Forns', FALSE, 'Traffic-Related Air Pollution, Noise at School, and Behavioral Problems in Barcelona Schoolchildren: A Cross-Sectional Study.', 'Environ Health Perspect. 2016; 124 (4): 529-535.', 2016, STRINGDECODE('Querschnittstudie an spanischen Schulkindern zur Untersuchung, ob die Verhaltensentwicklung und Hyperaktivit\u00e4t durch verkehrsbedingte Schadstoffe und Strassenl\u00e4rm in der Schule beeintr\u00e4chtigt wird.'), STRINGDECODE('2720 Schulkinder im Alter von 7 bis 11 Jahren (Durchschnitt 8.5 Jahre) aus 39 Schulen in Barcelona, welche gepaart nach hoch- bzw. tief mit NO2 belastetet, privat bzw. \u00f6ffentlich und Sozialstatus ausgew\u00e4hlt wurden. Die Sch\u00fcler waren seit mindestens 6 Monaten in derselben Schule eingeschrieben. BREATHE Projekt Spanien.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Die Eltern bewerteten das Verhalten der Kinder zu Beginn 2012 mit einem Test f\u00fcr psychologischen Schwierigkeiten (Strengths and difficulties questionnaire SDQ), welcher aus insgesamt 25 Fragen besteht. Der SDQ-Index reicht von 0 f\u00fcr keine Probleme bis 40 f\u00fcr verhaltensauff\u00e4llig. Die Lehrer wiederum wurden mit einem standardisierten Fragebogen nach Anzeichen von Hyperaktivit\u00e4t und Unaufmerksamkeit (ADHS, 18 Fragen) befragt. Der ADHD-DSM-IV-Index deutet mit steigender Punktezahl (0-54) auf Probleme hin. Die Schadstoffbelastung mit Black Carbon BC, elementarem Kohlenstoff EC und NO2 an den Schulen wurde mit Messkampagnen 2012/2013 in den gepaarten Schulen \u00fcber einen Zeitraum von je einer Woche in der warmen und der kalten Jahreszeit in einem Klassenzimmer und auf dem Schulhof durchgef\u00fchrt. Die Kombination der durchschnittlichen Schadstoffbelastungen w\u00e4hrend der beiden Messwochen ergaben unter Einbezug von saisonalen Schwankungen (Abgleich mit offiziellen Messdaten) die langfristigen Belastungen in den Schulen. Die Belastung an der Wohnadresse mit BC und mit NO2 wurde mit Landnutzungsmodellen nach der ESCAPE-Methode berechnet. Im Rahmen der Messkampagne wurde ausserdem die L\u00e4rmbelastung in dB(A) im Schulzimmer an verschiedenen Stellen vor Schulbeginn (9Uhr) gemessen.\r\nMit linearen Regressionsmodellen f\u00fcr gemischte Effekte und Zufallsvariablen f\u00fcr die Kinder und Schulen wurde das Verhalten und ADHS-Symptome in Abh\u00e4ngigkeit der Schadstoffbelastung und des L\u00e4rms untersucht. Danach wurden Mehrschadstoffmodelle gerechnet. Einbezogen wurden das Alter, Geschlecht, Bildung der Mutter, Rauchen, der sozio\u00f6konomische Status auf Quartierebene und die Luftqualit\u00e4t und L\u00e4rm am Wohnort der Kinder. In Sensitivit\u00e4tsanalysen wurde die Effektmodifikation durch die L\u00e4rmbelastung und der Einbezug weiterer St\u00f6rfaktoren untersucht. Russ. Feinstaub. Verkehr. Gehirn. ZNS. Querschnittstudie. BREATHE. Mehrschadstoffmodell. Spanien.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die Belastung an den Schulen betrug median in der Aussenluft 1.33 (Interquartilabstand IQR 0.86) \u00b5g EC/m3, 1.33 \u00b5g BC/m3 und 48.5 (22.3) \u00b5g NO2/m3 und in der Raumluft 1.19 (1.01) \u00b5g EC/m3, 1.37 \u00b5g BC/m3 und 29.8 (21) \u00b5g NO2/m3. Die L\u00e4rmbelastung im Schulzimmer betrug 38 (7.6) dB und die BC-Konzentration an der Wohnadresse 2.63 \u00b5g BC/m3.\r\nDas Verhalten der Kinder nach dem SDQ-Index war in Abh\u00e4ngigkeit der EC- und BC-Belastung aussen und innen, sowie der NO2-Belastung aussen im Einzel- wie auch im Mehrschadstoffmodell auff\u00e4lliger. Pro IQR-Unterschied von EC in der Innenluft und EC und NO2 in der Aussenluft war die Punktezahl des SDQ-Index um 1.08 (95%-CI: 1.02-1.14), 1.08 (1.03-1.13) und 1.08 (1.01-1.16) im Mehrschadstoffmodell unter Ber\u00fccksichtigung des L\u00e4rms signifikant erh\u00f6ht. Es gab keine Beziehung zwischen der SDQ-Bewertung und dem L\u00e4rm in der Schule. Die ADHS-Symptome hingegen waren nur mit dem L\u00e4rm, nicht aber mit den Verkehrsabgasen verbunden. Ein IQR-Unterschied des L\u00e4rms war sowohl unter Ber\u00fccksichtigung von EC wie auch NO2 (innen, resp. aussen) mit um 1.29 (1.18-1.43) und 1.24 (1.12-1.38), resp. 1.27 (1.15-1.4) und 1.26 (1.14-1.39) h\u00f6heren Punkten beim ADHS-Test signifikant verbunden. Im Mehrschadstoffmodell war ausserdem die ADHS-Bewertung in Abh\u00e4ngigkeit der EC-Belastung pro IQR-Unterschied paradox um 0.89 (0.82-0.96) signifikant erniedrigt.\r\nDie Autoren folgern, dass die Belastung mit verkehrsbedingten Schadstoffen in der Schule das Verhalten von Kindern im Primarschulalter beeintr\u00e4chtigen und dass L\u00e4rm ADHS-Symptome beg\u00fcnstigen k\u00f6nne.'), NULL, NULL, STRINGDECODE('BC war hoch korrliert (r=0.97) mit EC und die Effektsch\u00e4tzer der Beziehungen waren sehr \u00e4hnlich (und deshalb in der Studie nicht angegeben). Die BC-Belastung an der Wohnadresse war nicht mit den Zielgr\u00f6ssen verbunden.'), NULL, '1F', 1, TIMESTAMP '2016-12-19 16:26:10.156', 4, TIMESTAMP '2016-12-19 16:26:10.156', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(19, '10.1097/EDE.0000000000000405.', 26426942, 'Porta D, Narduzzi S, Badaloni C, Bucci S, Cesaroni G, Colelli V, Davoli M, Sunyer J, Zirro E, Schwartz J, Forastiere F.', 'Porta', FALSE, 'Air Pollution and Cognitive Development at Age 7 in a Prospective Italian Birth Cohort.', 'Epidemiology. 2016; 27 (2): 228-236.', 2016, STRINGDECODE('Geburtskohortenstudie in Italien zur Untersuchung, ob die kognitive Entwicklung von 7-j\u00e4hrigen Kindern durch die Schadstoffbelastung bei Geburt beeinflusst wird.'), '474 Kinder, welche im Zeitraum Juni 2003 bis Oktober 2004 in Rom zur Welt kamen und an der GASPII-Studie teilnahmen. Italien.', NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Nach der Geburt wurden die M\u00fctter in einem Interview \u00fcber ihre Schwangerschaft befragt. Im Alter des Kindes von 6 und 15 Monaten wurden die M\u00fctter telefonisch kontaktiert, mit 4, 7 und 8 Jahren wurden direkte Interviews gef\u00fchrt. Dabei wurde die Intelligenz (IQ) der Kinder  mit dem Wechsler-Intelligenztest (WISC-III) gepr\u00fcft. Dieser besteht aus den drei Hauptkomponenten verbaler IQ (VIQ), Leistungs-IQ (PIQ) und Gesamt-IQ (IQ), welche aus 10 der 13 Fragegruppen berechnet werden. Aus allen 13 Fragegruppen wurde ausserdem 4 weitere Komponenten, n\u00e4mlich Wortverst\u00e4ndnis (VCI), logisches Denken (POI), Ablenkungsfreiheit (FDI) und Verarbeitungsgeschwindigkeit gebildet. Mit Landnutzungsmodellen wurde die Belastung mit PM2.5, PM10-2.5, die Lichtabsorption von PM2.5 (Russ) und NO2 nach der ESCAPE-Methode an der Wohnadresse abgesch\u00e4tzt. Als Verkehrsindikatoren wurde die Verkehrsdichte auf der n\u00e4chsten Strasse (Fahrzeuge/Tag) und das Verkehrsaufkommen auf allen Strassen sowie auf allen Hauptstrassen innert 100m (Zahl der Fahrzeuge mal Streckenl\u00e4nge) evaluiert. Damit wurden die Belastung w\u00e4hrend der Geburt, zwischen der Geburt und dem Test und im Jahr vor dem Test abgesch\u00e4tzt, wobei Adresswechsel zeitgewichtet ber\u00fccksichtigt wurden.\r\nMit multiplen linearen Regressionsanalysen wurde die Beziehung zwischen den verschiedenen Komponenten des Intelligenztests mit den Luftschadstoffen und den Verkehrsindikatoren \u00fcberpr\u00fcft. Einbezogen wurde das Geschlecht, Alter beim Test, Bildung der Mutter und des Vaters, SES, Alter der Mutter, Rauchen w\u00e4hrend der Schwangerschaft und Anzahl \u00e4lterer Geschwister. Weitere Analysen zur \u00dcberpr\u00fcfung der Robustheit wurden durchgef\u00fchrt. Kohortenstudie. Kognitive Entwicklung. Gehirnentwicklung. ZNS. Feinstaub. Italien.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die durchschnittliche Schadstoffbelastung und Standardabweichung SD w\u00e4hrend der Schwangerschaft betrug 44.9 (SD 10) \u00b5g NO2/m3, 19.5 (2.2) \u00b5g PM2.5/m3, 16.8 (4) \u00b5g PM10-2.5 und 2.73 (0.6) 10e-5/m Lichtabsorption. Die Verkehrsdichte lag im Durchschnitt bei 3485 (8041) Fahrzeugen pro Tag, das Verkehrsaufkommen auf allen Strassen, resp. allen Hauptstrassen innert 100m bei 1''825 (2''825), resp. bei 1''605 (2''840) km/d.\r\nEin schlechteres Abschneiden beim verbalen IQ sowie dem Wortverst\u00e4ndnis war mit der NO2-Belastung w\u00e4hrend der Schwangerschaft f\u00fcr einen Belastungsunterschied von 10 \u00b5g NO2/m3 mit 1.4 (95%-CI: 0.2-2.6) und 1.4 (0.2-2.7) geringerer Punktezahl signifikant verbunden. F\u00fcr alle anderen Schadstoffe waren die Intelligenzindikatoren tendenziell ebenfalls erniedrigt, aber nie signifikant. Geringere Punktezahlen beim Gesamt-IQ, dem verbalen IQ und dem Wortverst\u00e4ndnis waren mit der Verkehrslast auf den Hauptstrassen, resp. auf allen Strassen innerhalb eines 100m Radius mit 2 (0.3-3.8), 1.8 (0.1-3.6) und 2.2 (0.4-4), resp. 1.9 (0.2-3.7), 1.8 (0.1-3.5) und 2.2 (0.4-4) f\u00fcr einen Anstieg von 4000 Fahrzeugkilometer/Tag verbunden. Es gab jedoch keinen Zusammenhang zwischen den Zielgr\u00f6ssen und der Verkehrsdichte. F\u00fcr die Belastung nach der Schwangerschaft bis zum Test, sowie ein Jahr vor dem Test wurden vergleichbare, aber weniger deutliche Effektsch\u00e4tzer berechnet.\r\nDie Autoren folgern, dass die Intelligenz (IQ), insbesondere Indikatoren der verbalen Intelligenz, mit der Schadstoffbelastung und dem Verkehr w\u00e4hrend der Schwangerschaft und der Kindheit zusammenh\u00e4ngen k\u00f6nnten.'), NULL, NULL, NULL, NULL, '1F', 1, TIMESTAMP '2016-12-19 16:27:57.964', 4, TIMESTAMP '2016-12-19 16:27:57.964', 4, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(20, '10.1164/rccm.201508-1633OC', 26680605, 'Turner MC, Jerrett M, Pope Iii CA, Krewski D, Gapstur SM, Diver WR, Beckerman BS, Marshall JD, Su J, Crouse DL, Burnett RT.', 'Turner', FALSE, 'Long-Term Ozone Exposure and Mortality in a Large Prospective Study.', 'Am J Respir Crit Care Med. 2016; 193 (10): 1134-1142.', 2016, STRINGDECODE('Neuere Analyse der Daten der amerikanischen ACS-Kohortenstudie zur Untersuchung, ob die Sterblichkeit in den USA mit der langfristigen Spitzenstundenkonzentration von Ozon im Sommer und der Feinstaub und NO2-Belastung zusammenh\u00e4ngt.'), STRINGDECODE('669\u2018046 Frauen und M\u00e4nner aus allen 50 US-Staaten und Puerto Rico, welche ab 1982 im Alter von mindestens 30 Jahren (durchschn. 56 Jahre) an einer Krebsvorsorgestudie (American Cancer Society ACS-CPS-II) teilnahmen. Das \u00dcberleben der Studienteilnehmer wurde 1984, 1986 und 1988 direkt erfragt und seit 1989 durch Verkn\u00fcpfung mit dem nationalen Sterberegister bis 2004 nach ICD-9 und ICD-10 erfasst. USA.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Die Lebensdaten der Teilnehmer der ACS-Studie wurden anf\u00e4nglich direkt durch die Familien mitgeteilt, danach in den Sterberegistern nachverfolgt, insgesamt \u00fcber 22 Jahre von 1982 bis 2004. Bei der ersten Befragung wurden Angaben \u00fcber demographische Eigenschaften wie Zivilstand, Bildung, BMI, Rauchen, Alkoholkonsum, Ern\u00e4hrung und Belastung am Arbeitsplatz erhoben. Auf Basis der Volksz\u00e4hlung 1990 waren zudem Angaben \u00fcber Haushaltseinkommen, Anteil Afroamerikaner und Spanier, Erwachsene mit H\u00f6herer Bildung, Armut und Arbeitslosigkeit im Quartier vorhanden. Die Daten f\u00fcr die Ozonbelastung stammten vom hierarchischen Bayes Modell der US EPA, welche in einer Aufl\u00f6sung von 36*36km die t\u00e4glichen 8-Std. Maximalkonzentrationen f\u00fcr die Jahre 2001-2006 berechnete. Mit diesen Daten wurde das Jahresmittel der Ozonbelastung f\u00fcr die Jahre 2002 bis 2004 und das Jahresmittel der Sommerozonbelastung (April bis September) f\u00fcr die Wohnadresse bei Studienbeginn berechnet. Zus\u00e4tzlich wurde f\u00fcr Teilnehmer im Osten der USA die Ozonbelastung in einer Aufl\u00f6sung von 12*12km mit Daten von 2001-2008 aus den Osten der USA berechnet. Die Feinstaub PM2.5-Belastung wurde mit einer Kombination von Landnutzungsmodellen und Bayesscher Maximum Entropy Interpolation mit Messdaten von 1999-2008 abgesch\u00e4tzt und in regionale beeinflusste und quellnahe Sch\u00e4tzer unterschieden. Die NO2-Konzentration wurde ebenfalls mit Landnutzungsmodellen in einer 10*10km Aufl\u00f6sung abgesch\u00e4tzt, in welche Monitordaten und Satellitendaten einflossen. \r\nDie Beziehung der Sterblichkeit insgesamt, an Herz-/Kreislaufkrankheiten plus Diabetes und Atemwegskrankheiten zur langj\u00e4hrigen durchschnittlichen Schadstoffbelastung, wurde mit proportionalen Hazard-Modellen nach Cox untersucht. Einbezogen wurden die bei Studienbeginn erhobenen individuellen Risikofaktoren und Risikofaktoren auf Ebene der Agglomerationen. In Mehrschadstoffmodellen wurde die Robustheit der Ergebnisse untersucht. Zus\u00e4tzlich wurde der Einfluss der Temperatur, der Anteil Klimaanlagen, Quartiergr\u00f6sse, H\u00f6he \u00fcber Meer untersucht. ACS-Kohortenstudie. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('In der Nachfolgezeit traten 237''201 Todesf\u00e4lle auf, wovon 105''039 an Herz-/Kreislaufkrankheiten inklusive Diabetes, 45''644 an isch\u00e4mischen Herzkrankheiten sowie 20''484 an Atemwegskrankheiten. Die durchschnittlichen Jahresmittel betrugen 76 (SD 8) \u00b5g Ozon/m3, 12.6 (3) \u00b5g PM2.5 und 22 (10) \u00b5g NO2/m3 mit einer Spanne von 53-118 \u00b5g Ozon/m3, 1.4-27.9 \u00b5g PM2.5/m3 und 2-72 \u00b5g NO2/m3. Die Schadstoffe waren wenig korreliert (r=-0.13 und 0.23).\r\nEin Unterschied der Ozon-Belastung von 20 \u00b5g/m3 war signifikant mit einem erh\u00f6hten Sterberisiko insgesamt HR 1.02 (95%-CI: 1.02-1.04), und f\u00fcr spezifische Erkrankungen ausser isch\u00e4mischen Herzkrankheiten und Lungenkrebs verbunden, z.B. Atemwegserkrankungen (HR1.14), Diabetes (1.14), Lungenentz\u00fcndung/Grippe (1.15) und Durchblutungserkrankungen plus Diabetes (1.03). Die Ergebnisse blieben im Zwei-Schadstoffmodell mit lokalem und regionalem PM2.5 robust, auch nach zus\u00e4tzlichem Einbezug von NO2. Sie waren vergleichbar, wenn die Sommerozonbelastung untersucht wurde.\r\nEin Unterschied der Belastung mit PM2.5 nahen Quellen war signifikant mit einem erh\u00f6hten Risiko f\u00fcr die Gesamtsterblichkeit (HR 1.3; 1.2-1.3) und Sterblichkeit an Herz-/Kreislauferkrankungen (1.45) verbunden, nicht aber an Atemwegserkrankungen und Herzstillstand oder Herzrhythmusst\u00f6rungen. Das Risiko war mit der regionalen Feinstaubbelastung weniger ausgepr\u00e4gt, war aber zus\u00e4tzlich mit der Sterblichkeit an Atemwegserkrankungen verbunden (1.16). Im Zwei-Schadstoffmodell mit Ozon schw\u00e4chten sich die Zusammenh\u00e4nge mit der regionalen PM2.5-Belastung ab, blieben aber signifikant und akzentuierten sich mit der lokalen Feinstaubbelastung. Ein Unterschied der NO2-Jahresbelastung von 20 \u00b5g/m3 war mit einer erh\u00f6hten Sterblichkeit insgesamt, an Herz-/Kreislaufkrankheiten (1.08; ausser Arrhythmien) und Lungenentz\u00fcndung und Grippe (1.11) verbunden. Es gab leichte Hinweise auf eine Effektschwelle f\u00fcr die pulmonale Sterblichkeit bei etwa 70\u00b5g/m3. Untergruppenanalysen zeigten, dass Personen unter 65 und solche ohne vorbestehende Atemwegserkrankungen ein h\u00f6heres ozonbedingtes Sterberisiko an Atemwegskrankheiten hatten und die Temperaturen das Sterberisiko beeinflusste (tiefe und hohe Temperaturen). Im Gegensatz zu fr\u00fcheren Analysen (ID6101) wurde ein Zusammenhang der Sterblichkeit an isch\u00e4mischen Herzkrankheiten mit der Ozonbelastung in Teilnehmern aus Kalifornien gefunden.\r\nDie Autoren schliessen, dass die langfristige Ozonbelastung unabh\u00e4ngig von Feinstaub und NO2 zur Sterblichkeit beitragen kann und dass auch PM2.5 und NO2 unabh\u00e4ngige Effekte zeigten.'), NULL, NULL, STRINGDECODE('Die Tabelle(n) zu den Effektsch\u00e4tzern aus den Mehrschadstoffmodellen mit NO2 fehlen.'), NULL, '1A', 273, TIMESTAMP '2016-12-19 10:14:23.349', 3, TIMESTAMP '2017-01-09 13:58:18.532', 3, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(21, '10.1183/13993003.01865-2014', 26405285, STRINGDECODE('Sigsgaard T, Forsberg B, Annesi-Maesano I, Blomberg A, B\u00f8lling A, Boman C, B\u00f8nl\u00f8kke J, Brauer M, Bruce N, H\u00e9roux ME, Hirvonen MR, Kelly F, K\u00fcnzli N, Lundb\u00e4ck B, Moshammer H, Noonan C, Pagels J, Sallsten G, Sculier JP, Brunekreef B.'), 'Sigsgaard', FALSE, 'Health impacts of anthropogenic biomass burning in the developed world.', 'Eur Respir J. 2015; 46 (6): 1577-1588.', 2016, STRINGDECODE('Positionspapier, welches narrativ einen \u00dcberblick \u00fcber die Trends und Auswirkungen der Biomasseverbrennung zur Energie- und W\u00e4rmeproduktion auf die Gesundheit in Europa gibt.'), STRINGDECODE('PubMed-Suche mit Schlagworten Holzrauch, h\u00e4usliches W\u00e4rmegewinnung, und USA, Kanada, Europa oder Australien und Gesundheit.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('\u00dcbersicht. Holz. Biomasse. Europa. Feinstaub PM2.5. PM10. Russ. Polyzyklische aromatische Kohlenwasserstoffe PAK. Emission. Atemwegserkrankungen. Herz-/Kreislaufkrankheiten.'), NULL, NULL, NULL, NULL, STRINGDECODE('Es wird gesch\u00e4tzt, dass es in der EU 65 Millionen Kleinfeuerungsanlagen (Kamine, \u00d6fen, Herde) zur direkten W\u00e4rmegewinnung mit Biomasse und 8 Millionen Biomasse betriebene Boiler gibt. Die europ\u00e4ische Direktive f\u00fcr erneuerbare Energien hat zum Ziel, die Energieerzeugung aus erneuerbaren Energien auf 20% zu erh\u00f6hen. Es kann davon ausgegangen werden, dass der Anteil an Feinstaubbelastung aus der Biomasseverbrennung steigt. Bereits heute k\u00f6nnen die Emissionen durch Biomasseverbrennung in l\u00e4ndlichen Gebieten mit wenig Verkehr in der gleichen Gr\u00f6ssenordnung liegen wie jene aus dem Verkehr in grossen St\u00e4dten (Sch\u00e4tzungen aus verschiedenen europ\u00e4ischen L\u00e4ndern). Die Nutzung von Biomasse als Energietr\u00e4ger wird sich zwischen 2005 und 2030 verdoppeln. Bis 2020 tr\u00e4gt die h\u00e4usliche Biomasseverbrennung 38%  zu den Gesamtemissionen von Feinstaub bei. 10-30% oder 1-4 \u00b5g/m3 der Feinstaub-Belastung stammt in europ\u00e4ischen L\u00e4ndern aus der Biomasseverbrennung; in alpinen T\u00e4lern kann dieser Wert 50% und mehr erreichen.\r\nDie Schadstoffemissionen aus der Biomasseverbrennung h\u00e4ngen ab von der Verbrennungstechnologie/Ofen, dem Verhalten der Nutzer (Brennmaterial: Holzgr\u00f6sse, Feuchtigkeit, Anfeuerungstechnik (h\u00f6chste Emissionen), Brenntemperatur und Wartung des Ofens) und Vorrichtungen zur Verminderung der Schadstoffemissionen. Bei einer Verbrennung von Abf\u00e4llen beispielsweise aus der Landwirtschaft oder Industrie wird mit h\u00f6heren Feinstaub- und Metallemissionen gerechnet. Der heutige Stand der Technik und das Verhalten der Nutzer sei noch weit entfernt vom technisch m\u00f6glichen. Relevant sind vor allem Emissionen von Feinstaub, Russ und PAK. Gem\u00e4ss der heutigen Datenlagen scheinen Produkte aus der Biomasseverbrennung genauso toxisch zu sein wie jene aus der Verbrennung fossiler Brennstoffe. Studien aus den USA, Kanada und D\u00e4nemark weisen auf kurz- und langfristige Zusammenh\u00e4nge verschiedener gesundheitlicher Zielgr\u00f6ssen mit der biomassebedingten Belastung in Innenr\u00e4umen und Draussen hin, z.B. der Sterblichkeit an Herz-/Kreislaufkrankheiten, Verschlechterung von Atemwegssymptomen und -gesundheit, tieferes Geburtsgewicht, Entz\u00fcndungszeichen im Blut. Experimentelle Studien weisen auf oxidativen Stress, Verringerung der Immunabwehr, Zell- und Gentoxizit\u00e4t und Entz\u00fcndungsprozesse wegen Holzrauch hin. Interventionsstudien zur Verminderung der Holzrauchbelastung deuten auf der anderen Seite auf einen Gesundheitsgewinn hin. Es wird gesch\u00e4tzt, dass in Europa mindestens 10% der Sterbelast (40''000 vorzeitige Todesf\u00e4lle pro Jahr) wegen Feinstaub auf die Biomasseverbrennung zur\u00fcckzuf\u00fchren ist.\r\nEine Luftreinhaltestrategie sollte gem\u00e4ss Autoren nicht nur Abgasvorschriften im Verkehrsbereich beinhalten, sondern auch f\u00fcr die Verbrennung von Biomasse Anwendung finden, da effiziente Technologien zur Verf\u00fcgung stehen. Die Autoren fordern ausserdem, die Forschung zu m\u00f6glichen Unterschiede zwischen Schadstoffen aus der Biomasseverbrennung und jenen aus der Verbrennung fossiler Brennstoffe zu vertiefen.'), NULL, NULL, NULL, NULL, '1F', 1, TIMESTAMP '2016-12-19 10:18:56.518', 3, TIMESTAMP '2016-12-19 10:18:56.518', 3, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(22, '10.1016/j.jash.2015.11.015', 26750378, 'Byrd JB, Morishita M, Bard RL, Das R, Wang L, Sun Z, Spino C, Harkema J, Dvonch JT, Rajagopalan S, Brook RD.', 'Byrd', FALSE, 'Acute increase in blood pressure during inhalation of coarse particulate matter air pollution from an urban location.', 'J Am Soc Hypertens. 2016; 10 (2): 133-139. e4.', 2016, STRINGDECODE('Experimentelle Studie an gesunden Personen zur Untersuchung, ob eine Belastung mit der gr\u00f6beren Feinstaubfraktion aus einem st\u00e4dtischen Umfeld den Blutdruck, die Gef\u00e4ssfunktion und die Herzfrequenzvariabilit\u00e4t beeinflusst.'), '29 gesunde Erwachsene im Alter von durchschnittlich 30 Jahren (18-50 Jahre), Nicht-Raucher und ohne Herz-/Kreislauferkrankungen, Atemwegskrankheiten, Hypertonie oder Diabetes. USA.', NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Die Probanden inhalierten via Gesichtsmaske doppelblind und \u00fcberkreuzt, mit zuf\u00e4lliger Reihenfolge im Abstand von jeweils 1-3 Wochen, eine aufbereitete Konzentration von PM10-2.5 Partikeln aus st\u00e4dtischer Aussenluft entlang des Detroit River in Michigan (beeinflusst durch Industrie, Verkehr, Kohlekraftwerke) und einmal gefilterte Luft. Die Exposition dauerte 2 Std. und die Probanden leisteten keine Arbeit, sie kamen nach einer Fastenzeit von mindestens 8 Std. zu den Tests. W\u00e4hrend den Belastungen wurden alle 7 Min. der systolische und diastolische Blutdruck, der Puls und kontinuierlich die Herzfrequenzvariabilit\u00e4t gemessen. Direkt nach der Belastung und 2 Std. sp\u00e4ter wurde mit Ultraschall der Durchmesser der Armarterie BAD und als Indikatoren f\u00fcr die Gef\u00e4ssfunktion die Pulswellengeschwindigkeit in der Aorta ermittelt. 2 Std. nach dem Ende der Exposition wurde die flussvermittelte Dilatation der Arterien FMD nach 5 Min. Okklusion mit Manschette am Oberarm mit Ultraschall gemessen (durchschnittlicher prozentualer Anstieg des Brachialisdurchmessers \u00fcber den Basiswert 50-70s nach Ablassen des Drucks). Die nach der Exposition untersuchten Zielgr\u00f6ssen wurden mit gepaarten T-Tests oder Wilcoxon-Tests (f\u00fcr nicht normal verteilte Daten) untersucht. Die mehrfach ermittelten Blutdruckwerte, der Puls und die Indikatoren der Herzfrequenzvariabilit\u00e4t wurden mit Modellen f\u00fcr gemischte Effekte analysiert und mit den Ergebnissen einer fr\u00fcheren Studie (ID7952) mit Partikeln aus dem l\u00e4ndlichen Umfeld verglichen. Experimentelle Studie. Gr\u00f6bere Fraktion. Feinstaub. Herz-/Kreislaufkrankheiten. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die erzielten inhalierten Konzentrationen betrugen im Durchschnitt 164 SD 80.4 \u00b5g PM10-2.5/m3 mit einer Spannweite von 70-440\u00b5g/m3. Bei den Reinluftinhalationen betrug die Durchschnittsbelastung 15.5 (6.4) \u00b5g/m3 mit einem Maximum von 27.4 \u00b5g/m3.\r\nDer systolische und diastolische Blutdruck w\u00e4hrend den Belastungen betrug 114.5 (95%-CI: 111-118) mmHg bzw. 75.7 (73-79) mmHg. Bei Feinstaubbelastung gegen\u00fcber Reinluft nahmen der systolische Blutdruck um 1.9 (1-2.8) mmHg zu, der diastolische Blutdruck um 1.9 (1.1-2.7) mmHg. Der Pulsdruck in der Aorta war direkt nach der Belastung mit Partikeln signifikant erh\u00f6ht. Der Puls und die Indikatoren f\u00fcr die Herzfrequenzvariabilit\u00e4t blieben unver\u00e4ndert. Gem\u00e4ss Autoren waren direkt nach der Belastung der Blutdruck in der Oberarmarterie tendenziell erh\u00f6ht und der Arteriendurchmesser BAD tendenziell geringer.\r\nEin Vergleich mit den Studienergebnissen von ID7952 mit Feinstaub aus l\u00e4ndlichen Gegenden zeigte insgesamt h\u00f6here Blutdruckwerte nach Belastung mit st\u00e4dtischem Feinstaub, aber einen kontinuierlicher Anstieg des Blutdrucks w\u00e4hrend der Belastung mit l\u00e4ndlichem Feinstaub w\u00e4hrend der Blutdruck direkt zu Beginn der Belastung mit st\u00e4dtischem Feinstaub anstieg und \u00fcber die Belastungsdauer stabil war.\r\nDie Autoren schliessen daraus, dass die gr\u00f6bere Fraktion des Feinstaubes unabh\u00e4ngig von ihrer Herkunft zu einem sofortigen Anstieg des Blutdrucks f\u00fchrt und die chemische Zusammensetzung eher sekund\u00e4r sei.'), NULL, NULL, STRINGDECODE('Vergleiche \u00e4hnliche Studie mit Partikeln aus dem l\u00e4ndlichen Raum ID 8453.'), NULL, '1F', 1, TIMESTAMP '2016-12-19 10:47:06.586', 3, TIMESTAMP '2016-12-19 10:47:06.586', 3, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(23, NULL, NULL, 'Rice MB, Rifas-Shiman SL, Litonjua AA, Oken E, Gillman MW, Kloog I, Luttmann-Gibson H, Zanobetti A, Coull BA, Schwartz J, Koutrakis P, Mittleman MA, Gold DR.', 'Rice', FALSE, 'Lifetime Exposure to Ambient Pollution and Lung Function in Children.', 'Med. 2016; 193 (8): 881-888', 2016, STRINGDECODE('Geburtskohortenstudie in den USA zur Untersuchung der Lungenfunktion bei Kindern in Abh\u00e4ngigkeit von der Feinstaub- und Verkehrsbelastung.'), STRINGDECODE('614 Kinder von durchschnittlich 8 Jahren in Massachusetts, geboren von M\u00fcttern, welche zwischen 1999 und 2002 f\u00fcr die VIVA-Kohorte in Boston und Umgebung durchschnittlich in der 10. Schwangerschaftswoche rekrutiert worden waren. USA.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Die M\u00fctter gaben bei Studieneintritt, in der 28. Schwangerschaftswoche, bei Geburt und danach j\u00e4hrlich ihre Adresse und Informationen zu Alter, Rasse, Bildung, Haushaltseinkommen und ihrer medizinische Anamnese. Die Kinder wurden im Alter von 6.6-10.9 Jahren vom Studienteam besucht. Dabei wurde die Lungenfunktion gemessen (FEV1, FVC, FEV1/FVC, FEF25-75). Um reversible Funktions\u00e4nderungen (bspw. erh\u00f6hter Muskeltonus) zu erkennen, wurde die Spirometrie nach einer Bronchiodilatation mit Albuterol wiederholt. Ausserdem wurde das Gewicht und die Gr\u00f6sse der Kinder gemessen.\r\nAnhand von offiziellen Verkehrsdaten und Karten wurde die Distanz zur n\u00e4chsten Hauptstrasse vom Wohnort der Teilnehmer (nat\u00fcrlicher Logarithmus) ermittelt. Mit einem Landnutzungsmodell, das auf Satellitendaten und station\u00e4ren Messwerten sowie geografischen und meteorologischen Gr\u00f6ssen beruhte, wurde die PM2.5-Belastung f\u00fcr die Wohnadresse abgesch\u00e4tzt. Die Russbelastung (Black Carbon, BC) wurde aus Messwerten von 148 Stationen unter Einbezug des Wetters, jahreszeitlicher Merkmale und der Verkehrsdichte des Jahres 2009 abgesch\u00e4tzt.\r\nDie Beziehung der Lungenfunktionswerte zu den Schadstoffen und zum Verkehr wurde separat f\u00fcr das erste Lebensjahr, die ganze Lebensspanne und das Jahr vor der Untersuchung mit linearen Regressionsmodellen unter Einbezug von Geschlecht, Alter, Gr\u00f6sse, Rasse, Rauchen im Haushalt, Einkommen und Bildung der Familie, Temperatur und Feuchtigkeit am Tag vor der Untersuchung und Jahreszeit analysiert. In einer zweiten Analyse wurde mit logistischen Regressionsmodellen f\u00fcr dieselbe Belastung die Beziehung zu klinisch relevanten Lungenfunktionsbeeintr\u00e4chtigungen (weniger als 80% des Normwertes nach GLI 2012) untersucht. Sensitivit\u00e4tsanalysen zur \u00dcberpr\u00fcfung der Robustheit wurden durchgef\u00fchrt. VIVA-Kohortenstudie. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die Lungenfunktionswerte betrugen im Durchschnitt 1.8 (SD 0.3) Liter FVC und 1.5 (0.3) l FEV1. Die durchschnittliche Schadstoffbelastung (und die Interquartilwerte) f\u00fcr das Jahr vor der Untersuchung betrugen 9.4 (2.6) \u00b5g PM2.5/m3, 0.46 (0.2) \u00b5g BC/m3, 21.9 (5.9) und die durchschnittliche Distanz zur n\u00e4chsten Hauptstrasse betrug 1''299m.\r\nDie Lungenfunktion hing sowohl mit der Strassenn\u00e4he bei Geburt und w\u00e4hrend der Untersuchung, als auch der PM2.5 und BC-Belastung ein Jahr vor der Untersuchung und w\u00e4hrend der ganzen Lebensspanne zusammen. Teilnehmer, welche n\u00e4her als 100m an einer Hauptstrasse wohnten hatten eine um 83 ml (95%CI: 10-156) geringere FEV1 und um 99ml (21-176) geringere FVC gegen\u00fcber solchen, welche 400m oder mehr von der n\u00e4chsten Hauptstrasse entfernt wohnten. Der Unterschied von 99ml Lungenvolumen entspricht etwa einem verz\u00f6gerten Lungenwachstum von 6 Monaten. Das Risiko einer Lungenfunktionsbeeintr\u00e4chtigung war f\u00fcr einen Unterschied von 2\u00b5g PM2.5/m3 ein Jahr vor der Untersuchung mit einer Odds Ratio f\u00fcr FEV1 von 1.41 (1.03-1.93) erh\u00f6ht. Alle weiteren Assoziationen waren schwach und durch weite Konfidenzintervalle gepr\u00e4gt.\r\nDie Autoren folgern, dass auch eine niedrige Feinstaubbelastung die Lungenfunktion von Kindern beeintr\u00e4chtigen k\u00f6nne.'), NULL, NULL, NULL, NULL, '1F', 1, TIMESTAMP '2016-12-19 10:51:07.943', 3, TIMESTAMP '2016-12-19 10:51:07.943', 3, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(24, NULL, NULL, STRINGDECODE('Schultz ES, Hallberg J, Bellander T, Bergstr\u00f6m A, Bottai M, Chiesa F, Gustafsson PM, Gruzieva O, Thunqvist P, Pershagen G, Mel\u00e9n E.'), 'Schultz', FALSE, 'Early-Life Exposure to Traffic-related Air Pollution and Lung Function in Adolescence.', 'Am J Respir Crit Care Med. 2016; 193 (2): 171-177.', 2016, STRINGDECODE('Geburtskohortenstudie in vier schwedischen Gemeinden zur Untersuchung, ob die Lungenfunktion im Alter von 16 Jahren mit der langfristigen Luftbelastung durch den Verkehr zusammenh\u00e4ngt.'), STRINGDECODE('2''287 Kinder der BAMSE-Kohorte, geboren 24.2.1994-20.11.1996 in Stockholm und Umgebung, die mit 1, 2, 4, 8 und 16 Jahren nachuntersucht wurden, und f\u00fcr welche Informationen \u00fcber die Luftbelastung und weitere Einflussfaktoren vorhanden waren. Schweden.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Im Alter der Kinder von 2 Monaten beantworteten die Eltern einen Fragebogen \u00fcber die Wohnadresse, Haushaltcharakteristiken etc., im Alter von 1, 2, 4 und 8 Jahren beantworteten sie Fragen nach Atemwegsymptomen. Mit 4, 8 und 16 Jahren wurden die Kinder auch klinisch untersucht (Lungenfunktion: FVC, FEV1, FEV0.5). Mit Dispersionsmodellen wurden die Belastungen durch quellenspezifische Schadstoffkonzentrationen (Verkehrs-NOx f\u00fcr die Jahre 1990, 1995, 2000, 2002, 2003 und 2004, PM10 nur f\u00fcr das Jahr 2004, unter Einbezug der Bebauung (Strassenschluchten) f\u00fcr ein Netz mit Maschenweite 500m (l\u00e4ndlich), 100m (st\u00e4dtisch) und 25m (Innerstadt) bestimmt. Von den Gemeinden wurden Informationen \u00fcber die altersabh\u00e4ngige durchschnittliche Aufenthaltszeit in Kinderkrippen und in den Schulen erhoben und berechnet, wie lange sich jedes Kind zu Hause, in einer Tagesst\u00e4tte oder Schule aufhielt. Mit den geocodierten Wohnadressen und Adressen der Schulen und Tagesst\u00e4tten wurde die individuelle zeitgewichtete NOx- und PM10-Belastung der Kinder f\u00fcr das erste Lebensjahr, die Jahre 1-8 und 8-16 Jahre gesch\u00e4tzt. \r\nDie Abh\u00e4ngigkeit der Lungenfunktion von der langfristigen Belastung mit PM10 und NOx wurde mit linearen Modellen und gemischten Modellen f\u00fcr die drei Lebensabschnitte untersucht. Mit ber\u00fccksichtigt wurden die Faktoren Gemeinde, Geschlecht, Alter, K\u00f6rpergr\u00f6sse und Gewicht. Die Analyse wurde ausserdem nach Geschlecht, Asthma, Passivrauchen und Rauchen der Mutter w\u00e4hrend der Geburt geschichtet. \r\nUm eine Gewichtung der Belastung w\u00e4hrend der verschiedenen Lebensabschnitte (1.,1-8, 8-16 Jahre) vorzunehmen, wurden mit einem Likelihood-Quotienten-Test alternative in die Lebensabschnitte eingebettete, gemischte Regressionsmodelle (ges\u00e4ttigtes Modell (Grundmodell), kritische-Phase Modell, Akkumulationsmodell, sensible-Periode Modell) gepr\u00fcft. Mit Akaike\u2018s Informationskriterium AIC wurden die Modelle untereinander verglichen. Geburtskohortenstudie BAMSE. Schweden.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die durchschnittlichen Schadstoffbelastungen durch den lokalen Verkehr lagen f\u00fcr NOx zwischen 0.5 \u00b5g/m3 und 123 \u00b5g/m3 und f\u00fcr PM10 zwischen 0.2 \u00b5g/m3 und 22 \u00b5g/m3 w\u00e4hrend des ersten Lebensjahres und nahm w\u00e4hrend der Studiendauer laufend ab.\r\nDie NOx-Belastung im ersten Lebensjahr war mit einer um 16 ml (95% CI: -2-34.0ml) geringeren FEV1 pro 10\u00b5g NOx/m3, zeitgewichtet \u00fcber alle Aufenthaltsorte, verbunden. Die Assoziation zwischen NOx im ersten Lebensjahr und der Lungenfunktion war f\u00fcr Buben (30ml (-2-59), Nicht-Astmathiker (17ml (-2-35)), Teilnehmer, bei welchen die Mutter nicht geraucht hatte (21ml (2-40)), und Teilnehmern, welche Passivrauchen nicht ausgesetzt waren (20ml (1-39)), noch ausgepr\u00e4gter. Die Beziehung von NOx in den sp\u00e4teren Jahren waren nicht mit der Lungenfunktion assoziiert. Die Resultate f\u00fcr PM10 waren \u00e4hnlich, aber nicht signifikant.\r\nDie gemischten Modelle zeigten eine Tendenz f\u00fcr die Exposition im ersten Lebensjahr mit geringeren FVC-Werten (12.5ml (-1.4-26.4). F\u00fcr die weiteren Lebensabschnitte (1-8 und 8-16) konnte die Lungenfunktion nicht mehr mit der Luftbelastung assoziiert werden. Die alternativen Modelle unterschieden sich nicht vom ges\u00e4ttigten Grundmodell. F\u00fcr das kritische-Phase Modell und das sensible Modell war eine hohe Belastung mit NOx im ersten Jahr mit einem geringeren FEV1 assoziiert. Das Akkumulationsmodell deutete ausserdem darauf hin, dass wiederholte Luftbelastung einen negativen Effekt auf die Lungenfunktion hatte. Im Vergleich mit den unteren Normwerten (z-Wert < -1.64 SD) zeigte sich f\u00fcr die NOx-Belastung im ersten Lebensjahr ein signifikanter Zusammenhang einer geringeren FEV1 und FVC. Die Odds Ratio f\u00fcr eine FEV1 unter dem Normwert betrug f\u00fcr eine um 10.9 \u00b5g/m3 h\u00f6here NOx-Belastung 3.8 (1.3-10.9) und f\u00fcr FVC  4.3 (1.2-15). \r\nDie Autoren schliessen daraus, dass die Verkehrsbelastung im ersten Lebensjahr die Lungenfunktion der Kinder negativ beeinflusse und sich bis zum 16. Lebensjahr nicht davon erhole.'), NULL, NULL, NULL, NULL, '1F', 7, TIMESTAMP '2016-12-19 10:54:13.22', 3, TIMESTAMP '2017-01-06 11:46:13.181', 3, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(25, '10.1016/j.envres.2016.04.026.', 27136671, 'Lavigne E, Yasseen AS 3rd, Stieb DM, Hystad P, van Donkelaar A, Martin RV, Brook JR, Crouse DL, Burnett RT, Chen H, Weichenthal S, Johnson M, Villeneuve PJ, Walker M.', 'Lavigne', FALSE, 'Ambient air pollution and adverse birth outcomes: Differences by maternal comorbidities.', 'Environ Res. 2016; 148: 457-466.', 2016, STRINGDECODE('Geburtenregisterstudie in Kanada zur Untersuchung, ob eine Beziehung zwischen geburtshilflichen Zielgr\u00f6ssen und der modellierten Schadstoffbelastung besteht und ob diese Beziehung durch Begleiterkrankungen der Mutter beeinflusst wird.'), '818''499, resp. 362''800 Einlingsgeburten zwischen 2005 und 2012 in Ontario, denen PM2.5 und NO2, resp. Ozon-Belastungswerte zugeordnet werden konnten. Kanada.', 'Ontario, Kanada', '818499 Einlingsgeburten', '2005-2012', 'PM2.5, NO2, Ozon', NULL, STRINGDECODE('Aus dem nationalen Geburtenregister wurden als geburtshilfliche Zielgr\u00f6ssen Fr\u00fchgeburt (Geburt vor der 37. Schwangerschaftswoche), zu tiefes Geburtsgewicht bei termingeborenen Kindern (<2500g) und zu tiefes Geburtsgewicht entsprechend dem Gestationsalter (SGA, Gewicht unterhalb der 10. Perzentile) entnommen. Ausserdem wurden die 6-stellige Postleitzahl der Adresse bei Geburt und pers\u00f6nliche Merkmale wie Geschlecht des Kindes, Alter der Mutter bei Geburt, Entbindungsart und Monat und Jahr der Geburt herausgelesen. Durch Verkn\u00fcpfung mit anderen Registern wurde die Pr\u00e4valenz von Rauchen w\u00e4hrend der Schwangerschaft und der sozio\u00f6konomische Status (SES) auf Quartierebene herausgelesen, sowie m\u00fctterliche Begleiterkrankungen wie Asthma, Bluthochdruck, Diabetes, Herzerkrankungen, Schwangerschaftsdiabetes und Pr\u00e4eklampsie. Die j\u00e4hrliche und monatliche Belastung an der Wohnadresse mit PM2.5 stammte von Satellitendaten, die die Belastung in einer Aufl\u00f6sung von 1 x 1km absch\u00e4tzte. Die durchschnittliche Jahresbelastung mit NO2 an der Wohnadresse wurde mit einem Landnutzungsmodell, das auch Satellitendaten von 2005-2011 einbezog, gewonnen. Die max. 8h-Belastung mit Ozon in den Jahren 2002-2009 wurde mit einer Aufl\u00f6sung von 21km2 von Satellitendaten und Kombination mit Messdaten zwischen 2008 und 2009 f\u00fcr die Empf\u00e4ngnis in warmen Monaten (April-Oktober) berechnet. Damit wurde die Belastung w\u00e4hrend der ganzen Schwangerschaft und w\u00e4hrend einzelner Schwangerschaftstrimester abgesch\u00e4tzt.\r\nMit Regressionsmodellen f\u00fcr gemischte Effekte wurde der Zusammenhang der geburtshilflichen Zielgr\u00f6ssen mit der Schadstoffbelastung unter Einbezug von sozio\u00f6konomische Eigenschaften des Quartiers, Geschlecht und Gestationsalter, Alter der Mutter bei Geburt, Rauchen, Zivilstand, Parit\u00e4t, st\u00e4dtisch oder l\u00e4ndlicher Wohnort, Geburtsland der Mutter, Jahreszeit, Geburtsjahr und Anteil von Minderheiten im Quartier. Danach wurde f\u00fcr alle Zielgr\u00f6ssen eine Effektmodifikation durch Begleiterkrankungen mittels Interaktionswert mit der Schadstoffbelastung \u00fcberpr\u00fcft. Verschiedene Sensitivit\u00e4tsanalysen wurden durchgef\u00fchrt. Registerbasierte Kohortenstudie. Kanada. Blutdruck. Herz-/Kreislaufkrankheiten. Feinstaub. Empfindliche Gruppe.'), 'Geburtenregisterstudie (Querschnitt)', STRINGDECODE('Fr\u00fchgeburt, zu tiefes Geburtsgewicht bei Termingeburt, SGA'), NULL, NULL, STRINGDECODE('6.3% der Geburten waren Fr\u00fchgeburten, 4.9% der termingeborenen Kinder hatten ein zu tiefes Geburtsgewicht und 9.3% der Geburten hatten ein zu tiefes Geburtsgewicht bezogen auf ihr Gestationsalter (SGA). Die durchschnittliche Schadstoffbelastung und die Interquartilwerte (IQR) \u00fcber die gesamte Schwangerschaft betrug 9.1 (2) \u00b5g PM2.5/m3, 30.4 (17.2) \u00b5g NO2/m3 und 55.5 (10) \u00b5g O3/m3. \r\nEin IQR-Anstieg von PM2.5, NO2 und Ozon \u00fcber die gesamte Schwangerschaft war mit einem erh\u00f6hten Risiko f\u00fcr Fr\u00fchgeburten verbunden 4% (95%-CI: 2.4-5.6), 8.4% (5.5-10.3) und 2% (0.5-4.1) und nur f\u00fcr die Ozonbelastung pro IQR-Abstand mit SGA 11.5% (9.9-13.1) und einem tiefen Geburtsgewicht 8.3 (4.6-11.5). \u00c4hnliche Effektsch\u00e4tzer wurden auch f\u00fcr die einzelnen Trimester berechnet.\r\nDie Wahrscheinlichkeit einer Fr\u00fchgeburt war bei Frauen mit Diabetes und Pr\u00e4eklampsie mit der PM2.5-Belastung erh\u00f6ht (10.6% (0.2-22.1) vs. 3.8% (2.2-5.4) bzw. 8.3% (0.8-16.4) vs. 3.8% (2.2-5.4) pro IQR), sowie mit der NO2-Belastung bei Diabetikerinnen erh\u00f6ht (23.8% (5.5-44.8) vs. 6.5% (3.7-8.4) pro IQR). Asthma beeinflusste die Beziehung zwischen der Fr\u00fchgeburt und der Ozonbelastung: Frauen ohne Asthma hatten pro IQR-Anstieg eine OR von 2% (0-3.5), Frauen mit Asthma hingegen eine von 12% (3.5-21.1). Die anderen \u00fcberpr\u00fcften Begleiterkrankungen beeinflussten die Verbindung zwischen der Luftverschmutzung und SGA und dem tiefen Geburtsgewicht nicht.\r\nDie Autoren folgern, dass die Beziehung zwischen den Fr\u00fchgeburten und der Luftverschmutzung durch Diabetes, Pr\u00e4eklampsie und Asthma beeinflusst worden sei.'), NULL, NULL, STRINGDECODE('Die Autoren haben ihren Fokus nicht auf Mehrschadstoffmodelle gerichtet, aber im Diskussionsteil angef\u00fcgt, dass die signifikanten Werte aus den Einschadstoffmodellen auch in den Mehrschadstoffmodellen erhalten blieben und f\u00fcr PM2.5 unter Diabetespatientinnen die Effekte paradox h\u00f6her waren.'), NULL, '1F', 1, TIMESTAMP '2016-12-19 15:49:39.473', 3, TIMESTAMP '2016-12-19 15:49:39.473', 3, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(26, '10.1016/j.jaci.2015.12.1309.', 26944405, STRINGDECODE('Mendola P, Wallace M, Hwang BS, Liu D, Robledo C, Mannist\u00f6 T, Sundaram R, Sherman S, Ying Q, Grantz KL.'), 'Mendola', FALSE, 'Preterm birth and air pollution: Critical windows of exposure for women with asthma.', 'J Allergy Clin Immunol. 2016; 138 (2): 432-440.e5.', 2016, STRINGDECODE('Geburtenregisterstudie in den USA zur Untersuchung, ob eine Beziehung zwischen Fr\u00fchgeburten und der modellierten Schadstoffbelastung besteht und ob diese Beziehung durch Asthma der Mutter beeinflusst wird.'), STRINGDECODE('223''502 Einlingsgeburten von 19 Spit\u00e4lern in den USA, im Zeitraum 2002-2008.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Aus den nationalen Geburtsregistern wurde die geburtshilfliche Zielgr\u00f6ssen Fr\u00fchgeburt (Geburt zwischen der 23. und der 37. Schwangerschaftswoche), sowie Informationen zur Entbindungsart, und m\u00fctterlicher und f\u00f6taler Eigenschaften entnommen. Die Fr\u00fchgeburten wurden in zwei Kategorien unterteilt: Fr\u00fchgeburt (Geburt vor der 37. SSW) und vorzeitige Fr\u00fchgeburt (<34. SSW). \r\nDa die Adressen der M\u00fctter nicht bekannt waren, wurde die Belastung der Mutter-Kindpaare f\u00fcr die Spitalregion mit dem CMAQ-Modell (ein Modell, das auf Emissionen, geografischen und meteorologischen Daten basiert) abgesch\u00e4tzt, das st\u00fcndliche Werte f\u00fcr die Schadstoffe lieferte. Dabei wurden die Schadstoffkonzentrationen von CO, NOx, Ozon, PM2.5, PM2.5 und SO2 in den 3 Monaten vor der Empf\u00e4ngnis, w\u00e4hrend aller SSW, w\u00e4hrend vier 7-Wochenperioden, f\u00fcr die letzten 6 Wochen der Schwangerschaft sowie \u00fcber die ganze Schwangerschaft berechnet.\r\nMit logistischen Regressionsanalysen mit verallgemeinerten Sch\u00e4tzgleichungen wurde das Risiko (Odds Ratio) f\u00fcr eine Fr\u00fchgeburt und eine vorzeitige Fr\u00fchgeburt (im Verh\u00e4ltnis zu allen fortlaufenden Schwangerschaften) zu den verschiedenen Belastungfenstern in Beziehung gesetzt, geschichtet f\u00fcr Frauen mit und ohne Asthma. Einbezogen wurde Alter der Mutter, Rasse, BMI vor der Schwangerschaft, Alkohol, Rauchen, Zivilstand, Spital, Parit\u00e4t, Versicherung und m\u00fctterliche Krankheiten. Danach wurde f\u00fcr beide Zielgr\u00f6ssen eine Effektmodifikation durch Asthma mittels Interaktionswert mit allen Schadstoffen \u00fcberpr\u00fcft. Sensitivit\u00e4tsanalysen wurden durchgef\u00fchrt und f\u00fcr Mehrfachtestung (Zufallspositive) wurde nach Benjamini und Hochberg korrigiert. Feinstaub. Registerbasierte Kohortenstudie. Empfindliche Gruppen. USA.'), NULL, NULL, NULL, NULL, STRINGDECODE('11.7% der Kinder kamen zu fr\u00fch zur Welt, davon  33.6% vor der 34. SSW. Die durchschnittliche Belastung und die Interquartilwerte (IQR) w\u00e4hrend der ganzen Schwangerschaft betrugen  643 (240) \u00b5g CO/m3, 59 (46.3) \u00b5g NOx/m3, 58.5 (15.6) \u00b5g O3/m3, 22.1 (4.42) \u00b5g PM10/m3, 11.9 (4.7) \u00b5g PM2.5/m3 und 10.6 (7.7) \u00b5g SO2/m3.\r\nIn der Analyse nach einzelnen Schwangerschaftswochen, hatten Frauen mit Asthma eine signifikante Interaktion gegen\u00fcber Frauen ohne Asthma f\u00fcr die 26. SSW und PM2.5, die 29. SSW und PM2.5 und PM10 und NOx, der 34. SSW und SO2 und die 34. SSW und CO, PM10 und SO2, wobei Ozon in der 34. SSW paradox mit der Fr\u00fchgeburt verbunden war. Unabh\u00e4ngig einer signifikanten Interaktion hatten M\u00fctter mit Asthma ein erh\u00f6htes Risiko einer Fr\u00fchgeburt f\u00fcr PM2.5 (SSW 26, 28 und 29), PM10 (SSW 28, 36), Ozon (SSW 33), SO2 (SSW 34, 36) und f\u00fcr CO (SSW 36). F\u00fcr Frauen ohne Asthma waren paradoxe Ergebnisse h\u00e4ufiger und ein erh\u00f6htes Risiko konnte nur f\u00fcr Ozon in den SSW 23, 26, 27 und 28, sowie f\u00fcr PM10 in der SSW 25 beobachtet werden.\r\nMit Ausnahme von Ozon war das Risiko einer Fr\u00fchgeburt in alle anderen Zeitfenstern f\u00fcr Frauen mit Asthma h\u00f6her. Die Belastung in den drei Monaten vor der Empf\u00e4ngis war f\u00fcr eine Fr\u00fchgeburt bei einem IQR-Anstieg von CO und NOx mit einer OR 1.12 (95%CI: 1.01-1.23) und 1.28 (1.13-1.45), sowie f\u00fcr eine vorzeitige Fr\u00fchgeburt mit NOx von 1.29 (1.06-1.57) verbunden. Pro IQR-Anstieg von CO und NOx in den ersten sieben Wochen war das Risko einer Fr\u00fchgeburt mit einer OR von 1.08 (1.00-1.15) und 1.13 (1.05-1.22), sowie f\u00fcr eine vorzeitige Fr\u00fchgeburt mit NOx von 1.22 (1.08-1.37) verbunden. Im Zeitraum der letzten sechs Wochen der Schwangerschaft war die PM10-Belastung mit der vorzeitigen Fr\u00fchgeburt mit einer OR von 1.13 (1.03-1.24) verbunden. Die PM2.5-Belastung \u00fcber die ganze Schwangerschaft ging mit einem erh\u00f6hten Risiko einer vorzeitigen Fr\u00fchgeburt mit einer OR von 1.11 (1.01-1.22) einher.\r\nDie Autoren folgern, dass das Risiko f\u00fcr Fr\u00fchgeburten in Abh\u00e4ngigkeit der Schadstoffbelastung unter Frauen mit Asthma h\u00f6her sei und v.a. in der 34-36 SSW signifikante Interaktionen beobachtet werden konnten, mit Ausnahme der Ozonbelastung, welche paradox verbunden war, bei Frauen ohne Asthma hingegen signifikant das Risiko einer Fr\u00fchgeburt erh\u00f6hte. Die Autoren schliessen ausserdem, dass fr\u00fche Belastungen auch schon vor der Schwangerschaft wichtige Belastungsfenster sein k\u00f6nnten.'), NULL, NULL, STRINGDECODE('Die Belastungsabsch\u00e4tzung erscheint ungenau. Die vielen unterschiedlichen Ergebnisse sind schwer zu interpretieren.'), NULL, '1F', 1, TIMESTAMP '2016-12-19 15:52:08.638', 3, TIMESTAMP '2016-12-19 15:52:08.638', 3, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(27, NULL, NULL, 'Halonen JI, Blangiardo M, Toledano MB, Fecht D, Gulliver J, Anderson HR, Beevers SD, Dajnak D, Kelly FJ, Tonne C.', 'Halonen', FALSE, 'Long-term exposure to traffic pollution and hospital admissions in London.', 'Environ Pollut. 2016; 208 (Pt A): 48-57.', 2017, STRINGDECODE('Registerstudie zur Untersuchung, ob die verkehrsbedingte Luftverschmutzung und die notfallm\u00e4ssigen Spitaleintritte wegen Herz-/Kreislaufkrankheiten und Atemwegserkrankungen in London zusammenh\u00e4ngen.'), STRINGDECODE('Alle notfallm\u00e4ssigen Spitaleintritte wegen Herz-/Kreislaufkrankheiten (ICD-10: I00-I99, separat wegen Herzkrankheiten I20-I25, Herzinsuffizienz I50 und Hirnschlag I61, I63, I64) sowie Atemwegskrankheiten (J00-J99, separat wegen Lungenentz\u00fcndung J12-J18, J20-J22 und Infektionen der unteren Atemwege J40-46) bei Einwohnern von London in den Jahren 2003-2010. England.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Unter Verwendung verschiedener Register (Gesundheitsinformationszentrum, nationale Statistikbeh\u00f6rde, Krebsregister) wurden im Untersuchungsgebiet, bestehend aus 27''731 Quartieren (COA, Volksz\u00e4hlungseinheiten) mit \u00fcber 8 Millionen Einwohnern innerhalb des Autobahnrings M25 die notfallm\u00e4ssigen Spitaleintritte und personenbezogene Informationen herausgelesen. Den Postleitzahlen der Adressen der Betroffenen wurden aus dem vom King''s College entwickelten Dispersionsmodell KCLurban (20x20m Aufl\u00f6sung) Jahresmittelbelastungen der Hintergrundbelastung mit PM10, PM2.5, PM10-2.5, Ozon und oxidierenden Gasen (Ox) sowie Werte f\u00fcr verkehrsbedingte Feinstaubbelastung (PM10, PM2.5) unterteilt in Abgase und Nicht-Abgase (Abrieb, Aufwirbelung Strassenstaub) und NOx und NO2 zugeordnet. Daraus wurde die bev\u00f6lkerungsgewichtete Belastung in den Quartieren \u00fcber die gesamte Studienperiode berechnet.\r\nMit linearen Poissonregressionen wurde die Beziehung zwischen den notfallm\u00e4ssigen Spitaleintritten und den verkehrsbedingten Schadstoffen und der Hintergrundluftbelastung unter Einbezug von Alter und Geschlecht untersucht. In einem weiteren Schritt wurde die Rasse und eine alternative Variable f\u00fcr das Rauchen (alters- und geschlechtsstandardisierte Sterbef\u00e4lle wegen Lungenkrebs im Quartier) sowie L\u00e4rm (Schalldruckpegel LAeq 16h, TRANEX) und dann zus\u00e4tzlich noch der Sozialstatus auf Quartiersebene (Deprivationsindex) einbezogen. Die Analyse wurde unter Ber\u00fccksichtigung einer nicht-linearen Beziehung mit abschnittsweisen (piecewise) linearen Modellen wiederholt. Alle Analysen wurden in zwei Alterskategorien (45-74, >74) f\u00fcr die Herz-/Kreislaufkrankheiten und in drei (0-14, 15-64, >65) f\u00fcr die Atemwegserkrankungen geschichtet durchgef\u00fchrt. Sensitivit\u00e4tsanalysen. Zeitreihenanalyse. England.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die durchschnittliche j\u00e4hrliche Belastung der Quartiere betrug f\u00fcr die verkehrsbedingten Schadstoffe 0.72 (0.33)  \u00b5g PM2.5 Abgas/m3, 0.8 (0.37)  \u00b5g PM10 Abgas/m3, 0.73 (0.31)  \u00b5g PM2.5 nicht-Abgas/m3, 2.46 (1.03)  \u00b5g PM10 nicht-Abgas/m3, 39  (8.7) \u00b5g NO2/m3 und 66.2 (21.3)  \u00b5g NOx/m3 und f\u00fcr die Hintergrundbelastung 15.3 (1.1)  \u00b5g PM2.5/m3, 24 (2)  \u00b5g PM10/m3, 8.7 (0.86)  \u00b5g PM10-2.5/m3, 38.7 (5.4)  \u00b5g Ozon/m3 und 77.7 (3.4)  \u00b5g NOx/m3. Die Luftschadstoffe waren untereinander sehr hoch miteinander korreliert.\r\nEs gab keinen erkennbaren Zusammenhang zwischen den Spitaleintritten wegen Herz-/Kreislaufkrankheiten oder Atemwegserkrankungen unter Verwendung von linearen Modellen.  In weiteren Analysen mit abschnittsweisen linearen Modellen unter Ber\u00fccksichtigung des Alters und des Geschlechts war das Risiko eines Spitaleintrittes mit allen Schadstoffen teilweise signifikant erh\u00f6ht. Unter Einbezug weiterer St\u00f6rfaktoren verschwanden diese Effekte allerdings. Der Sozialstatus auf Quartiersebene (Deprivationsindex) beeinflusste die Beziehung zwischen den Spitaleintritten und der NOx-Belastung. Das h\u00f6chste Deprivationsquintil hatte gegen\u00fcber dem Niedrigsten ein signifikant h\u00f6heres Risiko f\u00fcr Spitaleintritte sowohl wegen Herz-/Kreislaufkrankheiten als auch wegen Atemwegserkrankungen.\r\nDie Autoren folgern, dass ihre Studie keine \u00fcberzeugenden Hinweise einer Beziehung zwischen den Spitaleintritten wegen Herz-/Kreislaufkrankheiten und Atemwegserkrankungen und der langfristigen, verkehrsbedingten Luftbelastung lieferte, der Deprivationsindex allerdings das Risiko f\u00fcr Spitaleintritte beeinflussen k\u00f6nnte.'), NULL, NULL, NULL, NULL, '1F', 1, TIMESTAMP '2017-01-06 11:12:39.151', 3, TIMESTAMP '2017-01-06 11:12:39.151', 3, NULL, '');
INSERT INTO paper(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, COMMENT, INTERN, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, ORIGINAL_ABSTRACT,  RESULT_MEASURED_OUTCOME) VALUES
(28, '10.1016/j.scitotenv.2013.01.077', 23454700, 'Pascal M, Corso M, Chanel O, Declercq C, Badaloni C, Cesaroni G, Henschel S, Meister K, Haluza D, Martin-Olmedo P, Medina S, Aphekom group.', 'Pascal', FALSE, 'Assessing the public health impacts of urban air pollution in 25 European cities: results of the Aphekom project.', 'Sci Total Environ 2013; 449: 390-400.', 2017, STRINGDECODE('Quantifizierung der Spitaleintritte und Sterblichkeit durch Feinstaub und Ozon in 25 europ\u00e4ischen St\u00e4dten und Berechnung des gesundheitlichen Nutzens, wenn die Belastungen auf die von der WHO vorgeschlagenen Richtwerte gesenkt w\u00fcrden.'), STRINGDECODE('Einwohner von 25 St\u00e4dten der L\u00e4nder \u00d6sterreich, Belgien, Frankreich, Griechenland, Ungarn, Irland, Italien, Rum\u00e4nien, Slowenien, Spanien, Schweden, und Grossbritannien, insgesamt fast 39 Millionen Einwohner. APHEKOM-Projekt. Europa.'), NULL, NULL, NULL, NULL, NULL, STRINGDECODE('Die kurzfristigen Dosis-Wirkungsbeziehung f\u00fcr die Beziehungen der Spitaleintritte wegen Atemwegskrankheiten (ICD-9 460-519 und ICD-10 J00-J99) zu Ozon (h\u00f6chster 8 Std. Wert jeden Tages) und der Eintritte wegen Atemwegserkrankungen und Herzkrankheiten (ICD-9 390-429, ICD-10 I00-I52) zu Feinstaub (PM2.5 oder PM10) wurden aus der APHEA-Studie abgeleitet. Die langfristige Beziehung der Sterblichkeit bei Personen von mehr als 30 Jahren an Atemwegserkrankungen zur Ozonbelastung (nur im Sommer) wurde aus der amerikanischen ACS-Studie abgeleitet (Jerret 2009), die langfristige Beziehung der Sterblichkeit insgesamt und an Herz-/Kreislaufkrankheiten zur Feinstaubbelastung (PM2.5) ebenfalls aus der ACS-Studie (Pope 2002, 2004). Die Luftverschmutzung wurde anhand der Konzentrationen von Ozon und PM2.5 (PM10 wurde umgerechnet) der festen Messstationen in den Studiengebieten in den Jahren 2004-2006 beurteilt. Dann wurde der gesundheitliche Nutzen als eingesparte F\u00e4lle berechnet, wenn erstens die Jahresmittelwert um 5\u00b5g/m3 abnehmen, zweitens die Jahresmittelwerte auf die Richtwerte der WHO von 10\u00b5g PM2.5/m3 oder 20\u00b5g PM10/m3 abnehmen sowie f\u00fcr die akuten Folgen von Ozon die Abnahme der Spitzenwerte auf maximal 100\u00b5g/m3. Die langfristigen Auswirkungen wurden mit der Sterbetafel-Methode berechnet, die Ergebnisse als gewonnene Jahre (zus\u00e4tzliche Lebenserwartung) im Alter von 30 Jahren ausgedr\u00fcckt und zur Berechnung des Nutzens mit der Anzahl Einwohner in diesem Alter multipliziert. Der Nutzen bez\u00fcglich langfristiger Auswirkungen wurde mit dem Zahlungsbereitschaftsansatz monetarisiert, die akuten Folgen mit dem Ansatz der Krankheitskosten. Quantifizierung, Gesundheitsfolgenabsch\u00e4tzung. Europa.'), NULL, NULL, NULL, NULL, STRINGDECODE('Die maximale 8 Std. Ozonkonzentration betrug im Median 65\u00b5g/m3 (Bereich der Jahresmittelwerte 50-82.8\u00b5g/m3), in allen St\u00e4dten wurde der Richtwert der WHO von 100\u00b5g/m3 \u00fcberschritten. Der Median der PM2.5-Belastung betrug 16.6\u00b5g/m3 (Bereich 9.4-38.2\u00b5g/m3). In Dublin, Malaga und Stockholm wurde der Richtwert von 20\u00b5g PM10/m3 nicht \u00fcberschritten, nur in Stockholm wurde auch der Richtwert f\u00fcr PM2.5 von 10\u00b5g/m3 eingehalten.\r\nEine Abnahme des t\u00e4glichen 8 Std. H\u00f6chstwertes von Ozon w\u00fcrde bei Erwachsenen 380 Spitaleintritte, die Einhaltung des Richtwertes von 100\u00b5g/m3 mehr als 150 Eintritte wegen Atemwegskrankheiten verhindern. Eine Abnahme der Ozonkonzentration im Sommer um 5 \u00b5g Ozon/m3 w\u00fcrde langfristig 280 vorzeitige Todesf\u00e4lle bei Erwachsenen verhindern. Die Verminderung der PM10-Konzentration in 25 St\u00e4dten auf 20\u00b5g/m3 w\u00fcrde kurzfristig mehr als 8000 Spitaleintritte wegen Herz- und Lungenkrankheiten vermeiden, eine Abnahme um 5 \u00b5g PM10/m3 kurzfristig etwas \u00fcber 3000 Spitaleintritte verhindern. Eine Abnahme der PM2.5-Konzentration auf 10 \u00b5g/m3 w\u00fcrde die Lebenserwartung im Alter von 30 Jahren um Null (Stockholm) bis 22 Monate (Bukarest), im Median um 5.8 Monate verl\u00e4ngern, entsprechend j\u00e4hrlich fast 421''000 verlorenen Lebensjahren. Eine Abnahme der PM2.5/m3 um 5\u00b5g/m3 k\u00f6nnte die Lebenserwartung im Alter von 30 Jahren um 3-5 Monate (Median 4 Monate) verl\u00e4ngern, entsprechend j\u00e4hrlich 224''000 verlorenen Lebensjahren.\r\nDer gr\u00f6sste finanzielle Nutzen w\u00e4re mit einer Abnahme von PM2.5 auf einen Jahresmittelwert von 10\u00b5g/m3 verbunden, die Einsparungen von etwa 31 Milliarden Euro (95%CI 11-54 Mia.) zur Folge h\u00e4tte; in den St\u00e4dten betr\u00fcgen die Einsparungen (Spannweite) 20 Millionen Euro (Dublin) bis 5 Milliarden Euro (Athen).'), NULL, NULL, NULL, NULL, '1F', 1, TIMESTAMP '2017-01-06 11:18:10.465', 3, TIMESTAMP '2017-01-06 11:18:10.465', 3, NULL, '');
