INSERT INTO PUBLIC.PAPER(ID, DOI, PM_ID, AUTHORS, FIRST_AUTHOR, FIRST_AUTHOR_OVERRIDDEN, TITLE, LOCATION, PUBLICATION_YEAR, GOALS, POPULATION, POPULATION_PLACE, POPULATION_PARTICIPANTS, POPULATION_DURATION, EXPOSURE_POLLUTANT, EXPOSURE_ASSESSMENT, METHODS, METHOD_STUDY_DESIGN, METHOD_OUTCOME, METHOD_STATISTICS, METHOD_CONFOUNDERS, RESULT, RESULT_EXPOSURE_RANGE, RESULT_EFFECT_ESTIMATE, RESULT_MEASURED_OUTCOME, COMMENT, INTERN, ORIGINAL_ABSTRACT, MAIN_CODE_OF_CODECLASS1, VERSION, CREATED, CREATED_BY, LAST_MODIFIED, LAST_MODIFIED_BY, NUMBER) VALUES
(42, '10.1080/0284186X.2017.1298836', 28281859, STRINGDECODE('Koivum\u00e4ki T, Tujunen J, Vir\u00e9n T, Heikkil\u00e4 J, Sepp\u00e4l\u00e4 J.'), STRINGDECODE('Koivum\u00e4ki'), FALSE, 'Geometrical uncertainty of heart position in deep-inspiration breath-hold radiotherapy of left-sided breast cancer patients.', 'Acta Oncol. 2017;: 1-5.', 2017, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, STRINGDECODE('BACKGROUND: This study aimed to determine the geometrical uncertainty of the position of the heart with daily cone beam computed tomography (CBCT) during deep-inspiration breath-hold (DIBH) treatment of the left breast.\nMATERIAL AND METHODS: A visually guided optical respiratory monitoring system was used in DIBH treatment of 15 breast cancer patients. Heart position was determined in relation to the planning target volume (PTV) in 225 fractions in which daily low-dose CBCT images were compared with planning CT images. In addition, the position of the left lung apex and diaphragm was measured to evaluate the success of the DIBH.\nRESULTS: The median shift of the heart was 1\u2009mm to the left, 1\u2009mm superiorly and 0\u2009mm in the anterior-posterior (AP) direction during the treatment course when compared to the PTV position in planning CT. Based on these movements, an AP margin of 4\u2009mm, a lateral (LR) margin of 3\u2009mm, and a superior-inferior (SI) margin of 5\u2009mm should be added to the heart contour to ensure avoiding the heart when planning treatment. The distance between the left lung apex and diaphragm, applied as a surrogate for lung volume, was 2mm (median) smaller during the CBCT acquisitions than during the planning CT acquisition. The correlation coefficient between the surrogate of lung volume and the distance between the heart and PTV was r\u2009=\u2009.46 in the AP, r\u2009=\u2009.72 in the LR and r\u2009=\u2009.79 in the SI directions.\nCONCLUSION: Residual variation was observed in the position of the heart in comparison to PTV, even with a visually guided DIBH technique. These geometrical uncertainties should be taken into account when planning radiotherapy treatment. The success of DIBH may make a major contribution to the variation of the heart position during treatment.'), NULL, 1, TIMESTAMP '2017-03-11 17:43:09.724', 3, TIMESTAMP '2017-03-11 17:43:09.724', 3, 42),
